INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1, 'РАЗДЕЛ A', 'СЕЛЬСКОЕ ХОЗЯЙСТВО, ОХОТА И ЛЕСНОЕ ХОЗЯЙСТВО', 'Раздел  A  охватывает  использование  природных ресурсов растительного и животного происхождения. Раздел включает деятельность    по    выращиванию   сельскохозяйственных культур,  разведению животных (как сельскохозяйственных, так  и  диких),  заготовке  леса  и  сбору  дикорастущих лесопродуктов,   а   также   деятельность,  связанную  с воспроизводством и защитой лесов', NULL, NULL, 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(2, 'Подраздел AA', 'СЕЛЬСКОЕ ХОЗЯЙСТВО, ОХОТА И ЛЕСНОЕ ХОЗЯЙСТВО', '', 1, 'РАЗДЕЛ A', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(3, '01', 'Сельское хозяйство, охота и предоставление услуг в этих областях', 'Сельское хозяйство, охота и предоставление услуг в  этих областях В  группировке 01 прежде всего выделяются  два  основных вида деятельности: \n- производство    продукции    растениеводства     (01.1 "Растениеводство") \n- производство    продукции     животноводства     (01.2 "Животноводство") В   группировке   01 выделяется также охота и разведение диких   животных, включая   предоставление  услуг в этих областях.   В  группировке 01   также   классифицируется добыча морских млекопитающих (моржей, тюленей) В группировке 01.1 сделано различие между: \n- выращиванием   однолетних   полевых культур, таких как зерновые культуры,   овощные культуры и цветы (см. 01.11 и 01.12) \n- выращивание многолетних культур на виноградниках и  во фруктовых садах (см. 01.13) В группировке 01.2 виды деятельности   сгруппированы  по типам    животных,   а   не   по произведенной продукции (например   мясо, молоко, шкуры и кожи и т.п.), при этом не   делается различия между полным содержанием животных в стойлах   (непастбищным   скотоводством)  и   выгонным (пастбищным) скотоводством в открытом поле В   группировке   01.3   "Растениеводство  в сочетании с животноводством (смешанное сельское хозяйство)"  нарушен обычный принцип определения основного вида деятельности. Это обусловлено тем, что многие     сельскохозяйственные предприятия имеют более   или  менее    сбалансированное производство продукции растениеводства и животноводства, что  не  позволяет    классифицировать их деятельность в одной из группировок 01.1 или 01.2 Некоторые виды деятельности, такие как подготовка почвы, посев,    уборка    урожая,    управление  (менеджмент), являющиеся        обычной     частью        деятельности сельскохозяйственного предприятия, могут быть   выделены как услуги в области   растениеводства и животноводства, предоставляемые   сельскохозяйственным предприятиям   за вознаграждение или   на   договорной основе. Данные виды деятельности классифицируются   в   группировках 01.41 и 01.42 соответственно Деятельность в   области сельского хозяйства не включает последующую       переработку       сельскохозяйственной продукции,   кроме     первичной      обработки   данной продукции     (деятельность     по           последующей переработке       сельскохозяйственной         продукции классифицируется в группировке 15  "Производство пищевых продуктов,    включая   напитки"   и   группировке    16 "Производство табачных изделий") Однако,    как    исключение  из   общего   правила, для классификации    интегрированных   видов    деятельности деятельность    сельскохозяйственных        предприятий, перерабатывающих         собственную          продукцию, классифицируется   в   группировке 01, даже если  обычно производство этой продукции относится к группировке 15. Например: \n- выращивание винограда и производство из него вина \n- выращивание оливок и производство  из них   оливкового масла Этот  раздел   не   включает  землеустроительные  работы (например          террасирование       и       осушение сельскохозяйственных земель,  подготовку  рисовых  полей и  т.д.),  которые  классифицированы  в  группировке  45 "Строительство";   сбыт   и    изучение   рынка    через комиссионеров  и  кооперативные   организации,   которые классифицируются  в   разделе  G  "Оптовая  и  розничная торговля; ремонт автотранспортных  средств,  мотоциклов, бытовых  изделий   и   предметов  личного  пользования"; производство   шкур    и    кож   на   бойнях,   которое классифицируется в подразделе DA  "Производство  пищевых продуктов,  включая  напитки,   и   табака",   а   также спортивно-любительскую охоту,  которая  классифицируется в   группировке   92.62    "Прочая    деятельность    по организации  отдыха   и  развлечений,  не  включенная  в другие группировки"', 2, 'Подраздел AA', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(4, '01.1', 'Растениеводство', 'Растениеводство \nЭта группировка также включает: \n- выращивание  растений   под   укрывным  материалом  (в парниках, теплицах)', 3, '01', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(5, '01.11', 'Выращивание зерновых, технических и прочих сельскохозяйственных культур, не включенных в другие группировки', 'Выращивание зерновых,      технических      и     прочих сельскохозяйственных культур,  не  включенных  в  другие группировки', 4, '01.1', 8);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(6, '01.11.1', 'Выращивание зерновых и зернобобовых культур', 'Выращивание зерновых и зернобобовых культур \nЭта группировка включает: \n- выращивание твердой и  мягкой  пшеницы,  ржи,  ячменя, овса, кукурузы, риса и прочих зерновых культур \n- выращивание  и  сушку  зернобобовых  культур  (гороха, фасоли, бобов) \n- выращивание   семян,   в   том   числе    элитных    и репродукционных семян, предназначенных для реализации \nЭта группировка не включает: \n- выращивание сахарной кукурузы, см. 01.12.1', 5, '01.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(7, '01.11.2', 'Выращивание картофеля, столовых корнеплодных и клубнеплодных культур с высоким содержанием крахмала или инулина', 'Выращивание картофеля,    столовых    корнеплодных     и клубнеплодных культур с высоким содержанием крахмала или инулина \nЭта группировка включает: \n- выращивание картофеля \n- выращивание   столовых  корнеплодных  и  клубнеплодных культур с высоким содержанием крахмала или инулина \n- выращивание семян и посадочного материала, в том числе элитных   и   репродукционных,    предназначенных    для реализации \nЭта группировка не включает: \n- выращивание прочих овощей, см. 01.12.1', 5, '01.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(8, '01.11.3', 'Выращивание масличных культур', 'Выращивание масличных культур \nЭта группировка включает: \n- выращивание  семян   и   плодов   масличных   культур: подсолнечника,  сурепицы, горчицы, рапса, соевых бобов и других масличных культур \n- выращивание  элитных  семян  и  репродукционных семян, предназначенных для реализации', 5, '01.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(9, '01.11.4', 'Выращивание табака и махорки', 'Выращивание табака и махорки \nЭта группировка включает: \n- выращивание табака и махорки и их первичную обработку: сушку листьев табака и махорки \n- выращивание    семян,    в   том   числе   элитных   и репродукционных, предназначенных для реализации', 5, '01.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(10, '01.11.5', 'Выращивание сахарной свеклы', 'Выращивание сахарной свеклы \nЭта группировка включает: \n- выращивание сахарной свеклы \n- выращивание семян и маточников сахарной свеклы,  в том числе  элитных  и  репродукционных,  предназначенных для реализации', 5, '01.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(11, '01.11.6', 'Выращивание кормовых культур; заготовка растительных кормов', 'Выращивание кормовых   культур;  заготовка  растительных кормов \nЭта группировка включает: \n- выращивание корнеплодных  кормовых  культур  (кормовой свеклы,   кормовой   моркови,   турнепса),   бахчевых  и зернобобовых кормовых культур \n- выращивание  силосных кормовых культур (подсолнечника, кукурузы, кормовой капусты и т.п.) \n- выращивание однолетних и многолетних кормовых трав \n- заготовку  сена  и  производство  растительных  кормов (силоса, сенажа, травяной муки) \n- выращивание  семян  кормовых  культур,  в  том   числе элитных    и    репродукционных,   предназначенных   для реализации', 5, '01.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(12, '01.11.7', 'Выращивание прядильных культур', 'Выращивание прядильных культур \nЭта группировка включает: \n- выращивание     прядильных    культур  (льна-долгунца, обыкновенной конопли, хлопчатника и др.) \n- мочку   растений,  из  которых  получают  растительное текстильное волокно \n- выращивание  семян  прядильных  культур,  в  том числе элитных семян и репродукционных  семян,  предназначенных для реализации \nЭта группировка не включает: \n- выращивание растений,  используемых для плетения,  см. 02.01.5', 5, '01.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(13, '01.11.8', 'Выращивание прочих сельскохозяйственных культур, не включенных в другие группировки', 'Выращивание прочих   сельскохозяйственных   культур,  не включенных в другие группировки \nЭта группировка включает: \n- выращивание   растений,   используемых   в    качестве пестицидов \n- выращивание  растений,  используемых,  в  основном,  в парфюмерии, фармации и для аналогичных целей \n- выращивание хмеля \n- выращивание     сельскохозяйственных    культур,    не включенных в другие группировки \nЭта группировка не включает: \n- выращивание дынь, см. 01.12.1 \n- выращивание овощных семян, см. 01.12.1 \n- выращивание цветов, см. 01.12.2 \n- выращивание цветочных семян, см. 01.12.2 \n- выращивание декоративных растений, см. 01.12.2 \n- выращивание  культур  для  производства напитков,  см. 01.13.3 \n- выращивание  культур  для производства пряностей,  см. 01.13.4 \n- выращивание орехов, см. 01.13.22 \n- сбор лесных продуктов и прочих  дикорастущих  сырьевых материалов, см. 02.01.2', 5, '01.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(14, '01.12', 'Овощеводство; декоративное садоводство и производство продукции питомников', 'Овощеводство; декоративное  садоводство  и  производство продукции питомников', 4, '01.1', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(15, '01.12.1', 'Овощеводство', 'Овощеводство \nЭта группировка включает: \n- выращивание  томатов,  дынь,  лука,  капусты,  салата, огурцов, моркови,  столовой свеклы, фасоли, бобов, кресс \n-  салата,  сахарной  кукурузы,  кабачков,   баклажанов, лука \n- порея, перца \n- выращивание зеленых культур, используемых как приправы (каперсов,   укропа,   петрушки,   кервеля,   эстрагона, майорана) \n- выращивание семян овощных культур, в том числе элитных семян  и  репродукционных  семян,  предназначенных   для реализации \nЭта группировка не включает: \n- выращивание  картофеля и посадочного материала для его выращивания, см. 01.11.2 \n- выращивание   сахарной  свеклы  и  производство  семян сахарной свеклы, см. 01.11.5 \n- выращивание  корнеплодных  и  клубнеплодных  культур с высоким содержанием крахмала или  инулина,  производство семян  и  посадочного материала для их выращивания,  см. 01.11.2', 14, '01.12', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(16, '01.12.2', 'Декоративное садоводство и производство продукции питомников', 'Декоративное садоводство    и   производство   продукции питомников \nЭта группировка включает: \n- выращивание цветов \n- производство семян, луковиц и клубней цветов \n- выращивание рассады и растений для декоративных целей, включая выращивание дернины для пересадки \nЭта группировка не включает: \n- выращивание  культур  для производства пряностей,  см. 01.13.4 \n- выращивание лесопосадочных материалов, см. 02.01.5 \n- выращивание новогодних елок, см. 02.01.5', 14, '01.12', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(17, '01.12.3', 'Выращивание грибов, сбор лесных грибов и трюфелей', 'Выращивание грибов, сбор лесных грибов и трюфелей', 14, '01.12', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(18, '01.12.31', 'Выращивание грибов и грибницы (мицелия)', 'Выращивание грибов и грибницы (мицелия)', 17, '01.12.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(19, '01.12.32', 'Сбор лесных грибов и трюфелей', 'Сбор лесных грибов и трюфелей', 17, '01.12.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(20, '01.13', 'Выращивание фруктов, орехов, культур для производства напитков и пряностей', 'Выращивание фруктов,  орехов,  культур для  производства напитков и пряностей', 4, '01.1', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(21, '01.13.1', 'Выращивание винограда', 'Выращивание винограда \nЭта группировка включает: \n- выращивание винограда на вино и столового винограда \n- производство  вина  из  винограда,  выращенного данным хозяйством \n- выращивание    посадочного    материала    виноградных насаждений \nЭта группировка не включает: \n- производство вина из винограда,  выращенного не данным хозяйством, см. 15.93', 20, '01.13', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(22, '01.13.2', 'Выращивание прочих фруктов и орехов', 'Выращивание прочих фруктов и орехов', 20, '01.13', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(23, '01.13.21', 'Выращивание плодовых и ягодных культур', 'Выращивание плодовых и ягодных культур \nЭта группировка включает: \n- выращивание яблок, слив, вишни, цитрусовых, абрикосов, персиков,  смородины,  малины,   крыжовника,   земляники (клубники) и других плодовых культур', 22, '01.13.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(24, '01.13.22', 'Выращивание орехов', 'Выращивание орехов', 22, '01.13.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(25, '01.13.23', 'Выращивание посадочного материала плодовых насаждений', 'Выращивание посадочного материала плодовых насаждений', 22, '01.13.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(26, '01.13.24', 'Сбор плодов, ягод и орехов, в том числе дикорастущих', 'Сбор плодов, ягод и  орехов, в  том  числе  дикорастущих', 22, '01.13.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(27, '01.13.3', 'Выращивание культур для производства напитков', 'Выращивание культур для производства напитков \nЭта группировка включает: \n- выращивание чая \n- производство       зеленого       чайного        листа (неферментированного),     черного     чайного     листа (ферментированного) и частично ферментированного чайного листа \nЭта группировка не включает: \n- выращивание хмеля \n- обработку чайного листа, см. 15.86', 20, '01.13', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(28, '01.13.4', 'Выращивание культур для производства пряностей', 'Выращивание культур для производства пряностей \nЭта группировка включает: \n- выращивание     сельскохозяйственных    культур    для производства  пряностей:  лаврового   листа,   базилика, кориандра, аниса, тмина и т.п.', 20, '01.13', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(29, '01.2', 'Животноводство', 'Животноводство \nЭта группировка не включает: \n- предоставление услуг по содержанию животных и уходу за ними, см.01.42 \n- производство щипаной шерсти, см. 15.11.2', 3, '01', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(30, '01.21', 'Разведение крупного рогатого скота', 'Разведение крупного рогатого скота \nЭта группировка включает: \n- разведение мясного и молочного крупного рогатого скота \n- разведение племенного крупного рогатого скота \n- производство  сырого  коровьего молока и сырого молока прочего крупного рогатого скота \n- производство бычьей спермы \nЭта группировка не включает: \n- переработку  коровьего молока за пределами фермы,  см. 15.51', 29, '01.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(31, '01.22', 'Разведение овец, коз, лошадей, ослов, мулов и лошаков', 'Разведение овец, коз, лошадей, ослов, мулов и лошаков', 29, '01.2', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(32, '01.22.1', 'Разведение овец и коз', 'Разведение овец и коз \nЭта группировка включает: \n- разведение овец и коз \n- разведение племенных овец и коз \n- производство сырого овечьего и козьего молока \n- производство сырой (немытой) шерсти и пуха \n- производство спермы баранов и козлов \nЭта группировка не включает: \n- переработку козьего молока  за  пределами  фермы,  см. 15.51 \n- предоставление услуг по стрижке овец, см. 01.42', 31, '01.22', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(33, '01.22.2', 'Разведение лошадей, ослов, мулов и лошаков', 'Разведение лошадей, ослов, мулов и лошаков \nЭта группировка включает: \n- разведение лошадей, ослов, мулов и лошаков \n- разведение племенных лошадей и ослов \n- производство сырого кобыльего молока \n- производство спермы жеребцов и ослов \nЭта группировка не включает: \n- производство щипаной шерсти, см. 15.11.2 \n- переработку  кобыльего молока за пределами фермы,  см. 15.51 \n- деятельность конюшен для содержания скаковых и беговых лошадей и школ верховой езды, см. 92.62', 31, '01.22', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(34, '01.23', 'Разведение свиней', 'Разведение свиней \nЭта группировка включает: \n- разведение свиней, в том числе племенных животных \n- производство спермы хряков', 29, '01.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(35, '01.24', 'Разведение сельскохозяйственной птицы', 'Разведение сельскохозяйственной птицы \nЭта группировка включает: \n- разведение  сельскохозяйственной птицы (индеек,  уток, кур,  гусей,  цесарок)  на  мясо,  племенной   птицы   и молодняка \n- производство    куриных    яиц    и     яиц     прочей сельскохозяйственной птицы \nЭта группировка не включает: \n- разведение прочей птицы, см. 01.25.9 \n- производство пера и пуха, см. 15.12.2', 29, '01.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(36, '01.25', 'Разведение прочих животных', 'Разведение прочих животных', 29, '01.2', 9);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(37, '01.25.1', 'Разведение пчел', 'Разведение пчел \nЭта группировка включает: \n- разведение пчел \n- производство пчелиного меда и пчелиного воска', 36, '01.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(38, '01.25.2', 'Разведение кроликов и пушных зверей в условиях фермы', 'Разведение кроликов и пушных зверей в условиях фермы \nЭта группировка включает: \n- производство пушно \n- мехового сырья \nЭта группировка не включает: \n- производство шкур и кож, получаемых в результате охоты или ловли животных, см. 01.50', 36, '01.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(39, '01.25.3', 'Разведение шелкопряда', 'Разведение шелкопряда \nЭта группировка включает: \n- производство коконов шелкопряда', 36, '01.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(40, '01.25.4', 'Разведение оленей', 'Разведение оленей \nЭта группировка включает: \n- производство сырых и консервированных пантов', 36, '01.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(41, '01.25.5', 'Разведение верблюдов', 'Разведение верблюдов \nЭта группировка включает: \n- производство сырого верблюжьего молока', 36, '01.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(42, '01.25.6', 'Разведение домашних животных', 'Разведение домашних животных \nЭта группировка не включает: \n- дрессировку домашних животных, см. 93.05', 36, '01.25', 0);
