INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(45, '01.25.81', 'Разведение водных пресмыкающихся и лягушек в водоемах', 'Разведение водных пресмыкающихся и лягушек в водоемах \nЭта группировка включает: \n- разведение водных пресмыкающихся и лягушек в водоемах \n- разведение улиток \n- производство продукции разведения лягушек и улиток', 44, '01.25.8', 0);
