INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(48, '01.3', 'Растениеводство в сочетании с животноводством (смешанное сельское хозяйство)', 'Растениеводство в сочетании с животноводством (смешанное сельское хозяйство)', 3, '01', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(49, '01.30', 'Растениеводство в сочетании с животноводством (смешанное сельское хозяйство)', 'Растениеводство в    сочетании     с     животноводством (смешанное сельское хозяйство) \nЭта группировка включает: \n- растениеводство  в  сочетании  с  животноводством  при установленном коэффициенте специализации в одной области \nЭта группировка не включает: \n- смешанные      растениеводческие      и      смешанные животноводческие   хозяйства,   см.   их   основной  вид деятельности', 48, '01.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(50, '01.4', 'Предоставление услуг в области растениеводства, декоративного садоводства и животноводства, кроме ветеринарных услуг', 'Предоставление  услуг    в    области   растениеводства, декоративного  садоводства   и    животноводства,  кроме ветеринарных услуг', 3, '01', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(51, '01.41', 'Предоставление услуг в области растениеводства и декоративного садоводства', 'Предоставление  услуг   в   области   растениеводства  и декоративного садоводства', 50, '01.4', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(52, '01.41.1', 'Предоставление услуг, связанных с производством сельскохозяйственных культур', 'Предоставление  услуг,   связанных    с    производством сельскохозяйственных культур Эта   группировка   включает  предоставление    услуг  в области  растениеводства   за   вознаграждение   или  на договорной основе, таких как: \n- подготовка полей \n- посев сельскохозяйственных культур \n- возделывание   и   выращивание    сельскохозяйственных культур \n- опрыскивание   сельскохозяйственных   культур,  в  том числе с воздуха \n- обрезка фруктовых деревьев и виноградной лозы \n- пересаживание риса, рассаживание свеклы \n- уборка  урожая   и   подготовка   сельскохозяйственных культур  к  сбыту  на  рынке,  т.е.  очистка,   обрезка, сортировка,   сушка,    обеззараживание   (дезинфекция), покрытие  воском,   полирование,  обертывание,  удаление шелухи,  вымачивание,    охлаждение     и   упаковывание крупными  партиями,   включая  герметичное  упаковывание \n- предоставление услуг по проверке и  подготовке  семян, включая сушку,  очистку,  сортировку,  протравливание  и т.д. \nЭта группировка также включает: \n- защиту растений  от  болезней  и  вредителей  (включая кроликов и зайцев) \n- проверку и подготовку семян,  включая сушку,  очистку, сортировку, протравливание и т.д. \n- предоставление  сельскохозяйственных  машин  вместе  с экипажем и оператором \nЭта группировка не включает: \n- удаление   (рубку)   деревьев   в   целях   подготовки земельных    участков       для    сельскохозяйственного использования, см. 02.01 \n- подготовку растительных волокон к прядению, см. 17.1 \n- расчистку    земельных      участков      в      целях сельскохозяйственного использования, см. 45 \n- сбыт через комиссионеров и кооперативные  организации, см. 51 \n- деятельность  агрономов   и   экономистов   в  области сельского хозяйства, см. 74.14 \n- организацию сельскохозяйственных ярмарок  и  выставок, см. 74.84 \n- ремонт сельскохозяйственной техники, см. 29.32.9', 51, '01.41', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(53, '01.41.2', 'Предоставление услуг по закладке, обработке и содержанию садов, парков и других зеленых насаждений', 'Предоставление   услуг   по   закладке,   обработке    и содержанию садов, парков  и  других  зеленых  насаждений Эта  группировка   включает   предоставление   услуг  по декоративному садоводству для создания,  эксплуатации  и переустройства ландшафтов, таких как: \n- парки и сады для частных и  общественных жилых  домов, общественных  и    полуобщественных    зданий    (школы, больницы, административные здания,  церковные  здания  и т.п.),  городских   территорий   (парки,  зеленые  зоны, кладбища   и  т.п.),   зеленых  зон  вдоль  транспортных магистралей   (дороги,   железнодорожные   и  трамвайные линии, водные  пути,  порты),  промышленных  и  торговых зданий \n- озелененные зоны зданий  (сады  на  крышах, озеленение фасадов, внутренних помещений) \n- спортивные площадки,  игровые  площадки,  лужайки  для приема солнечных ванн, поля для гольфа  и  прочие  места для развлечений и отдыха \n- стационарные   и    проточные    водные   пространства (водоемы,   чередующиеся    влажные   участки,    пруды, плавательные бассейны,  рвы,  водные  трассы,  заводские сточные системы) \n- озеленение и благоустройство зон для защиты  от  шума, ветра, эрозии, яркого света и т.п. \n- меры по благоустройству для  защиты  окружающей  среды и   природы,    такие    как     сохранение    ландшафта (восстановление   природы,   восстановление   плодородия земель,  улучшение  земель,  сохранение  природных  зон, предохранение от обмеления водоемов и т.п.) \n- разведение древесных пород, обрезку деревьев  и  живой изгороди, пересадку больших деревьев \nЭта группировка не включает: \n- выращивание леса, см. 02.01.5 \n- планирование  и   проектирование  садов,    парков   и спортивных сооружений, см. 74.20', 51, '01.41', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(54, '01.41.3', 'Предоставление услуг по эксплуатации мелиоративных систем', 'Предоставление услуг   по   эксплуатации   мелиоративных систем', 51, '01.41', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(55, '01.42', 'Предоставление услуг в области животноводства, кроме ветеринарных услуг', 'Предоставление услуг  в  области  животноводства,  кроме ветеринарных услуг Эта  группировка   включает   предоставление  услуг   за вознаграждение или на договорной основе, таких как: \n- искусственное осеменение животных \n- обследование состояния стада, перегонка скота,   выпас скота, выхолащивание сельскохозяйственной птицы,  чистка курятников и т.п. \n- стимулирование разведения, прироста и   продуктивности животных \n- содержание сельскохозяйственных  животных  и  уход  за ними \n- по стрижке овец \n- по сортировке яиц \nЭта группировка не включает: \n- предоставление услуг по откорму скота, см. 01.2 \n- предоставление  услуг   по   развитию  охоты,  включая ловлю животных, в коммерческих целях, см. 01.50 \n- сбыт через комиссионеров и кооперативные  организации, см. 51 \n- предоставление   услуг  агрономами  и  экономистами  в области сельского хозяйства, см. 74.14 \n- предоставление ветеринарных услуг, см. 85.2', 50, '01.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(56, '01.5', 'Охота и разведение диких животных, включая предоставление услуг в этих областях', 'Охота и    разведение    диких     животных,     включая предоставление услуг в этих областях', 3, '01', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(57, '01.50', 'Охота и разведение диких животных, включая предоставление услуг в этих областях', 'Охота и    разведение    диких     животных,     включая предоставление услуг в этих областях \nЭта группировка включает: \n- охоту, включая ловлю животных, для получения продуктов питания,   шкур,   кожи   или   для   использования    в исследовательских  целях,  в  зоопарках  или  в качестве домашних животных \n- производство  пушнины,  кожи  пресмыкающихся  и шкурок птиц в результате охоты, в том числе ловли животных \n- разведение диких животных \n- предоставление услуг по развитию охоты,  в  том  числе ловли животных, в коммерческих целях \nЭта группировка также включает: \n- добычу морских млекопитающих (моржей, тюленей и т.п.) \nЭта группировка не включает: \n- производство  пушнины,  кожи  пресмыкающихся  и шкурок птиц в результате разведения  животных  на  фермах,  см. 01.25 \n- разведение животных на фермах,  кроме диких животных с целью сохранения вида, см. 01.25 \n- добычу китов, см. 05.01.1 \n- производство шкур и кож на бойнях, см. 15.11.2 \n- спортивно \n- любительскую охоту, см. 92.62', 56, '01.5', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(58, '02', 'Лесное хозяйство, лесозаготовки и предоставление услуг в этих областях', 'Лесное хозяйство, лесозаготовки и  предоставление  услуг в этих областях \nЭта группировка включает: \n- деятельность,   связанную     со     сбором     лесных дикорастущих материалов \n- производство лесоматериалов и продукции лесозаготовок, которая  подверглась   небольшой   обработке,  например, топливной древесины  или  деловой  древесины  (крепежных стоек, балансовой древесины и т.п.) \nЭта группировка не включает: \n- дальнейшую обработку древесины, начиная  с  распиловки и строгания, см. 20 \n- производство древесного угля, см. 24', 2, 'Подраздел AA', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(59, '02.0', 'Лесное хозяйство и предоставление услуг в этой области', 'Лесное хозяйство и предоставление услуг в этой области', 58, '02', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(60, '02.01', 'Лесоводство и лесозаготовки', 'Лесоводство и лесозаготовки', 59, '02.0', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(61, '02.01.1', 'Лесозаготовки', 'Лесозаготовки \nЭта группировка включает: \n- заготовку  и   первичную  переработку   лесоматериалов хвойных и лиственных пород, в том числе  при  проведении рубок главного пользования, промежуточного  пользования, прочих рубок: \n- валку леса \n- раскряжевку хлыстов на сортименты \n- заготовку сортиментов \n- заготовку деревьев с кроной \n- производство топливной древесины \nЭта группировка не включает: \n- производство древесной щепы, см. 20.10.2', 60, '02.01', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(62, '02.01.2', 'Сбор дикорастущих и недревесных лесопродуктов', 'Сбор дикорастущих и недревесных лесопродуктов \nЭта группировка включает: \n- сбор   смол,   растительных   соков,   трав,   мхов  и лишайников,  используемых для декоративных  целей,  сбор дикорастущих лубяных и других растений, используемых для плетения, набивки, окрашивания или дубления \n- сбор семян для выращивания дикорастущих растений \nЭта группировка не включает: \n- выращивание и сбор грибов и трюфелей, см. 01.12.3 \n- сбор ягод и орехов, см. 01.13.24', 60, '02.01', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(63, '02.01.5', 'Лесоводство', 'Лесоводство \nЭта группировка включает: \n- выращивание   леса:  выращивание,  посадку,  подсадку, пересадку  саженцев,  прореживание  и  охрану  лесов   и лесосек \n- выращивание  молодого  порослевого  леса   и   деловой древесины \n- выращивание новогодних елок \n- выращивание растительных материалов,  используемых для плетения \n- выращивание сеянцев деревьев и кустарников', 60, '02.01', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(64, '02.01.6', 'Деятельность лесопитомников', 'Деятельность лесопитомников', 60, '02.01', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(65, '02.01.61', 'Выращивание сеянцев, деревьев и кустарников', 'Выращивание сеянцев, деревьев и кустарников', 64, '02.01.6', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(66, '02.01.69', 'Выращивание прочей продукции питомников', 'Выращивание прочей продукции питомников', 64, '02.01.6', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(67, '02.02', 'Предоставление услуг в области лесоводства и лесозаготовок', 'Предоставление услуг    в    области    лесоводства    и лесозаготовок', 59, '02.0', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(68, '02.02.1', 'Предоставление услуг в области лесоводства', 'Предоставление услуг в области лесоводства \nЭта группировка включает: \n- предоставление услуг по таксации леса,  оценке леса (в аспекте промышленного использования) \n- предоставление  услуг  по  защите леса от вредителей и болезней \n- предоставление  услуг  по  защите  леса  от  пожара  и авиационной охране лесов, включая тушение лесных пожаров с воздуха \n- предоставление услуг по лесомелиорации \n- предоставление услуг лесоустроительными предприятиями, контрольными станциями лесных семян, лесными, почвенными и    нормативно   \n-   исследовательскими   станциями   и лабораториями', 67, '02.02', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(69, '02.02.2', 'Предоставление услуг в области лесозаготовок', 'Предоставление услуг в области лесозаготовок \nЭта группировка включает: \n- предоставление  услуг  по  транспортированию  бревен в пределах леса и др.', 67, '02.02', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(70, 'РАЗДЕЛ B', 'РЫБОЛОВСТВО, РЫБОВОДСТВО', 'Рыболовство  \n-  это использование морских и пресноводных рыбных  и  прочих  биоресурсов  посредством  лова  рыбы, ракообразных, моллюсков и сбора прочих морских продуктов (например  жемчуга,  губок  и  т.п.).  Рыбоводство \n- это разведение  рыбы,  включая  декоративных  рыб,  и прочих водных биоресурсов. В этом разделе классифицируются виды деятельности,  которые,  как  правило,  включают процесс производства  за  собственный  счет (например разведение устриц  для  производства  жемчуга).  Раздел не включает строительство    и    ремонт   судов   (см.   35.11)   и спортивно-любительское  рыболовство (см. 92.62), а также переработку    рыбы,   ракообразных   и   моллюсков   на рыбозаводах,  расположенных  на  берегу,  и на судах, не осуществляющих     лов     рыбы     (переработка    рыбы классифицируется    в    группировке    15.20).   Однако переработка  рыбы  на  судах,  осуществляющих  лов рыбы, классифицирована в данном разделе', NULL, NULL, 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(71, 'Подраздел BA', 'РЫБОЛОВСТВО, РЫБОВОДСТВО', '', 70, 'РАЗДЕЛ B', 1);
