INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(74, '05.01', 'Рыболовство', 'Рыболовство', 73, '05.0', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(75, '05.01.1', 'Рыболовство в открытых районах Мирового океана и внутренних морских водах', 'Рыболовство  в   открытых  районах  Мирового  океана   и внутренних морских водах \nЭта группировка включает: \n- рыболовство  в  открытых  районах  Мирового  океана  и внутренних морских водах \n- добычу китов \n- добычу морских ракообразных и моллюсков \n- добычу морских черепах, асцидий и других  оболочников, морских ежей и т.п. \nЭта группировка также включает: \n- добычу морских губок, кораллов  и  морских  водорослей \n- сбор раковин  и  панцирей  моллюсков,  ракообразных  и иглокожих и т.п. \n- добычу природного и культивированного жемчуга \n- переработку рыбы, ракообразных и  моллюсков  на  борту рыболовных судов \nЭта группировка не включает: \n- добычу морских млекопитающих,  кроме  китов,  например моржей, тюленей, см. 01.50 \n- спортивно-любительское  рыболовство  и  предоставление услуг в этой области, см. 92.62 \n- переработку рыбы, не связанную с рыболовством, т.е. на судах, не осуществляющих непосредственно лов  рыбы,  или на рыбозаводах, расположенных на берегу, см. 15.20', 74, '05.01', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(76, '05.01.11', 'Вылов рыбы и водных биоресурсов в открытых районах Мирового океана и внутренних морских водах сельскохозяйственными товаропроизводителями', 'Вылов рыбы  и  водных  биоресурсов  в  открытых  районах Мирового    океана    и    внутренних    морских   водах сельскохозяйственными товаропроизводителями', 75, '05.01.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(77, '05.01.12', 'Вылов рыбы и водных биоресурсов в открытых районах Мирового океана и внутренних морских водах несельскохозяйственными товаропроизводителями', 'Вылов рыбы  и  водных  биоресурсов  в  открытых  районах Мирового   океана    и    внутренних    морских    водах несельскохозяйственными товаропроизводителями', 75, '05.01.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(78, '05.01.2', 'Рыболовство в реках, озерах, водохранилищах и прудах', 'Рыболовство в реках, озерах, водохранилищах и прудах \nЭта группировка включает: \n- рыболовство в реках, озерах, водохранилищах и прудах \n- добычу пресноводных ракообразных и моллюсков \n- сбор  раковин  и  панцирей  пресноводных  моллюсков  и ракообразных \nЭта группировка не включает: \n- спортивно-любительское  рыболовство  и  предоставление услуг в этой области, см. 92.62', 74, '05.01', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(79, '05.01.21', 'Вылов рыбы и водных биоресурсов в реках, озерах, водохранилищах и прудах сельскохозяйственными товаропроизводителями', 'Вылов рыбы   и   водных  биоресурсов  в  реках,  озерах, водохранилищах    и     прудах     сельскохозяйственными товаропроизводителями', 78, '05.01.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(80, '05.01.22', 'Вылов рыбы и водных биоресурсов в реках, озерах, водохранилищах и прудах несельскохозяйственными товаропроизводителями', 'Вылов рыбы  и  водных  биоресурсов  в   реках,   озерах, водохранилищах    и    прудах    несельскохозяйственными товаропроизводителями', 78, '05.01.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(81, '05.01.3', 'Предоставление услуг в области рыболовства', 'Предоставление услуг в области рыболовства \nЭта группировка включает: \n- предоставление услуг по аэрорыбразведке \n- предоставление услуг по рыбоохране и рыбнадзору \n- предоставление услуг по регулированию вылова рыбы', 74, '05.01', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(82, '05.02', 'Рыбоводство', 'Рыбоводство', 73, '05.0', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(83, '05.02.1', 'Воспроизводство рыбы и водных биоресурсов', 'Воспроизводство рыбы и водных биоресурсов \nЭта группировка включает: \n- разведение устричной  молоди,  мидий,  молоди  омаров, лангустов, креветок, крабов, мальков рыб \n- селекцию,   выращивание   и   содержание  устриц   для употребления в пищу \n- селекцию,   выращивание    и    содержание   моллюсков (жемчужниц) для получения жемчуга \n- выращивание  красных  водорослей  и  других  съедобных морских водорослей \n- морское и пресноводное рыбоводство, включая разведение декоративных рыб \nЭта группировка не включает: \n- разведение лягушек, см. 01.25.81 \n- деятельность заказников для спортивного   рыболовства, см. 92.62', 82, '05.02', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(84, '05.02.11', 'Воспроизводство рыбы и водных биоресурсов сельскохозяйственными товаропроизводителями', 'Воспроизводство рыбы      и      водных      биоресурсов сельскохозяйственными товаропроизводителями', 83, '05.02.1', 0);
