INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(87, 'РАЗДЕЛ C', 'ДОБЫЧА ПОЛЕЗНЫХ ИСКОПАЕМЫХ', 'Раздел     включает    добычу    полезных    ископаемых, встречающихся  в  природе  в  виде твердых пород (уголь, руда),  в  жидком  состоянии  (нефть) или в газообразном состоянии  (природный  газ).  Добыча полезных ископаемых может  осуществляться  способами  подземной  и  открытой разработки  месторождений,  а  также  путем эксплуатации скважин.  Этот  раздел  включает  процессы,  связанные с транспортированием  и  реализацией  минерального  сырья, такие  как  дробление,  размалывание, обогащение, сушку, сжижение  природного  газа и агломерацию твердых топлив. Добыча    полезных    ископаемых    классифицируется   в группировках данного раздела по виду основного полезного ископаемого.    Подраздел    CA   относится   к   добыче топливно-энергетических   полезных   ископаемых   (угля, бурого  угля  (лигнита),  торфа, углеводородов, урановой руды); подраздел CB включает добычу полезных ископаемых, кроме    топливно-энергетических   (металлических   руд, различных  минералов,  продуктов  разработки  карьеров). Некоторые   технологические   процессы,   относящиеся  к данному  разделу,  в  частности  процессы,  связанные  с добычей   углеводородов,   могут   также  осуществляться специализированными   предприятиями  по  заказу  третьих сторон  в  качестве производственной услуги. Этот раздел также включает: \n- агломерацию угля и руд Этот раздел не включает: \n-   переработку  извлеченных  полезных  ископаемых  (см. раздел D) \n-  производство природных минеральных и других природных вод, осуществляемое у источников и скважин, см. 15.98 \n-   дробление,   измельчение  и  другие  виды  обработки некоторых   грунтов,   горных   пород  и  минералов,  не связанные  с  операциями  по  добыче полезных ископаемых подземным или открытым способами, см. 26.81, 26.82 \n- сбор, очистку и распределение воды, см. 41.00 \n-  подготовку  участков для проведения горных работ, см. 45.11 \n- разведку месторождений полезных ископаемых, см. 74.20', NULL, NULL, 2);
