INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(110, '11', 'Добыча сырой нефти и природного газа; предоставление услуг в этих областях', 'Добыча сырой нефти  и  природного  газа;  предоставление услуг в этих областях \nЭта группировка включает  деятельность  по  эксплуатации и/или  разработке   нефтяных  и  газовых  месторождений. Подобная  деятельность   может   включать   оснащение  и оборудование    скважин,     эксплуатацию    промысловых сепараторов,      деэмульгаторов,        илоотделителей, нефтепромысловых трубопроводов для  сырой  нефти  и  все прочие виды деятельности по подготовке нефти  или  газа, осуществляемые от места их  добычи  до  пункта  отгрузки (поставки).  Эта   группировка   включает  добычу  сырой нефти,  добычу   горючих   (битуминозных)    сланцев   и битуминозных песков и извлечение из  них  нефти,  добычу природного газа  и  жидких  углеводородов  (конденсата). Услуги,  связанные   с   бурением    или   эксплуатацией нефтяных  и   газовых   скважин   и  предоставляемые  за вознаграждение    или     на     договорной      основе, классифицированы в группировке 11.20 \nЭта группировка не включает: \n- производство нефтепродуктов, см. 23.20 \n- разведочное бурение, см. 45.12 \n- геофизические  исследования  и  геофизическую  съемку, см. 74.20', 88, 'Подраздел CA', 2);
