INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(114, '11.10.11', 'Добыча сырой нефти', 'Добыча сырой нефти \nЭта группировка включает: \n- добычу сырой нефти \n- декантацию, обезвоживание, обессоливание нефти \nЭта группировка не включает: \n- добычу нефтяного (попутного) газа, см. 11.10.12 \n- предоставление услуг по добыче нефти, см. 11.20 \n- производство нефтепродуктов, см. 23.20 \n- эксплуатацию трубопроводов,  кроме  внутрипромысловых, см. 60.30 \n- разведку    нефтяных    месторождений     и     другие геофизические,   геологические       и      сейсмические исследования, см. 74.20', 113, '11.10.1', 0);
