INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(117, '11.10.14', 'Добыча горючих (битуминозных) сланцев, битуминозного песка и озокерита', 'Добыча  горючих  (битуминозных)  сланцев,  битуминозного песка и озокерита \nЭта группировка включает: \n- добычу  горючих  (битуминозных) сланцев, битуминозного песка и озокерита \n- производство сырой  нефти  из  горючих  (битуминозных) сланцев и озокерита', 113, '11.10.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(118, '11.10.2', 'Добыча природного газа и газового конденсата', 'Добыча природного газа и газового конденсата \nЭта группировка включает: \n- добычу,  обезвоживание   и  сепарацию  фракций  жидких углеводородов  и   производство   сырого   газообразного углеводорода (природного газа) \n- извлечение метана, этана, бутана и  пропана  на  месте добычи \n- десульфурацию газа \n- добычу газового конденсата \nЭта группировка также включает: \n- извлечение гелия и сероводорода \nЭта группировка не включает: \n- предоставление услуг по добыче газа, см. 11.20 \n- эксплуатацию трубопроводов, см. 60.30 \n- разведку    газовых    месторождений     и      другие геофизические,     геологические       и    сейсмические исследования, см. 74.20', 112, '11.10', 2);
