INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(124, '11.20.1', 'Предоставление услуг по бурению, связанному с добычей нефти, газа и газового конденсата', 'Предоставление услуг по бурению,  связанному  с  добычей нефти, газа и газового конденсата \nЭта группировка включает: \n- наклонно  \n-  направленное бурение и повторное бурение; ударное бурение \nЭта группировка не включает: \n- геофизические          исследования           скважин, геолого \n- разведочные  и  сейсморазведочные работы,  см. 74.20.2', 123, '11.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(125, '11.20.2', 'Предоставление услуг по монтажу, ремонту и демонтажу буровых вышек', 'Предоставление услуг  по  монтажу,  ремонту  и демонтажу буровых вышек \nЭта группировка включает: \n- монтаж буровой установки на месте,  ремонт и демонтаж, цементирование обсадных труб нефтяных и газовых скважин, откачку скважин, заглушку и консервацию скважин и т.п.', 123, '11.20', 0);
