INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(131, '12.00.1', 'Добыча и обогащение (сортировка) урановых руд', 'Добыча и обогащение (сортировка) урановых руд \nЭта группировка включает: \n- добычу урановой руды \n- первичное обогащение урановой руды \n- производство желтого кека (концентрата урана) \nЭта группировка не включает: \n- производство обогащенного урана, см. 23.30 \n- производство   делящихся   и  воспроизводящих  ядерных материалов, см. 23.30 \n- производство металлического урана, см. 23.30', 130, '12.00', 2);
