INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(138, '13.10', 'Добыча и обогащение железных руд', 'Добыча и обогащение железных руд \nЭта группировка включает: \n- добычу подземным и  открытым  способами  руд,  ценных, преимущественно, содержанием в них железа \n- подготовку руд к обогащению: дробление  и  измельчение руд, промывку руд, разделение по  крупности  (грохочение и классификацию) \n- обогащение и агломерацию железных руд \nЭта группировка не включает: \n- добычу и обогащение серного  и  магнитного  колчедана, см. 14.30 \n- обжиг серного колчедана, см. 24.13', 137, '13.1', 3);
