INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(153, '13.20.41', 'Добыча руд и песков драгоценных металлов (золота, серебра и металлов платиновой группы)', 'Добыча руд   и   песков  драгоценных  металлов  (золота, серебра и металлов платиновой группы) \nЭта группировка включает: \n- извлечение драгоценных металлов из коренных  (рудных), россыпных   и  техногенных  месторождений  с  получением концентратов   и   других   полупродуктов,    содержащих драгоценные металлы', 152, '13.20.4', 0);
