INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(164, '14', 'Добыча прочих полезных ископаемых', 'Добыча прочих полезных ископаемых \nЭта группировка включает  не  только  добычу  ископаемых из  карьеров,   но    также    разработку   аллювиальных отложений,   скальных   пород,   соляных  месторождений. Получаемые продукты  используются,  главным  образом,  в строительстве  (например   песок,   камень  и  т.п.),  в производстве  материалов   (например  глина,   гипс    и т.п.), в производстве химических веществ и т.п.  В  этой группировке    также    классифицируется    производство рафинированной   соли,    пригодной    для   потребления человеком \nЭта группировка не включает  процессы  обработки  (кроме дробления, измельчения,  обогащения,  сушки,  сортировки и смешивания) добываемых полезных ископаемых', 135, 'Подраздел CB', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(165, '14.1', 'Разработка каменных карьеров', 'Разработка каменных карьеров', 164, '14', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(166, '14.11', 'Добыча камня для памятников и строительства', 'Добыча камня для памятников и строительства \nЭта группировка включает: \n- добычу   открытым   способом,   первичную   обработку, распиловку, дробление и  измельчение  мрамора,  гранита, песчаника   и   другого    камня    для   строительства, внутренней  и  внешней  облицовки зданий  и  сооружений, изготовления    художественных,       декоративных     и хозяйственных изделий \nЭта группировка не включает: \n- добычу минерального сырья для  химических  производств и производства удобрений, см. 14.30 \n- резку,  обработку  и  отделку  строительного  камня  и камня для памятников вне карьеров, см. 26.70', 165, '14.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(167, '14.12', 'Добыча известняка, гипсового камня и мела', 'Добыча известняка, гипсового камня и мела \nЭта группировка включает: \n- добычу, дробление и измельчение известняка \n- добычу гипсового камня и ангидрита \n- добычу мела и некальцинированного доломита \nЭта группировка не включает: \n- добычу минерального сырья для химических   производств и производства удобрений, см. 14.30 \n- производство кальцинированного (обожженного) доломита, см. 26.52', 165, '14.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(168, '14.13', 'Добыча сланцев', 'Добыча сланцев \nЭта группировка не включает: \n- добычу горючих (битуминозных) сланцев, см. 11.10.13', 165, '14.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(169, '14.2', 'Добыча гравия, песка и глины', 'Добыча гравия, песка и глины', 164, '14', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(170, '14.21', 'Разработка гравийных и песчаных карьеров', 'Разработка гравийных и песчаных карьеров \nЭта группировка включает: \n- добычу и промывку гравия,  песка  и  песчано-гравийной смеси для промышленности и строительства \n- дробление и измельчение и промывку гравия и песка \n- производство щебеночной смеси для  дорожных  покрытий, в том числе гудронированных,  из шлака  или  аналогичных промышленных отходов \nЭта группировка не включает: \n- добычу битуминозного песка, см. 11.10.13 \n- добычу минерального сырья для  химических  производств и производства удобрений, см. 14.30', 169, '14.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(171, '14.22', 'Добыча глины и каолина', 'Добыча глины и каолина \nЭта группировка включает: \n- добычу глины (включая огнеупорной) и каолина \nЭта группировка не включает: \n- добычу минерального сырья для химических производств и производства удобрений, см. 14.30', 169, '14.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(172, '14.3', 'Добыча минерального сырья для химических производств и производства удобрений', 'Добыча минерального  сырья  для химических производств и производства удобрений', 164, '14', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(173, '14.30', 'Добыча минерального сырья для химических производств и производства удобрений', 'Добыча минерального сырья для химических  производств  и производства удобрений \nЭта группировка включает: \n- добычу и обогащение природных фосфатов \n- добычу и обогащение природных калийных солей,  включая карналлит \n- добычу природной серы \n- добычу и обогащение серного и магнитного колчедана \n- добычу природного сульфата бария (барита) и  карбоната бария (витерита), природных боратов, природных сульфатов магния (кизерита), цеолитов и мышьяка \n- добычу минеральных красителей и плавикового шпата \nЭта группировка также включает: \n- добычу гуано \n- добычу сапропеля \nЭта группировка не включает: \n- добычу и производство соли, см. 14.40 \n- обжиг серного колчедана, см. 24.13 \n- производство   химических    удобрений    и    азотных соединений, см. 24.15', 172, '14.3', 0);
