INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(175, '14.40', 'Добыча и производство соли', 'Добыча и производство соли \nЭта группировка включает: \n- добычу поваренной  соли  из  подземных  месторождений, включая растворение и выкачивание \n- производство поваренной соли выпариванием  из  морской воды или других соленых вод \n- производство рассолов и прочих солевых растворов \n- измельчение, очистку и рафинацию поваренной соли \nЭта группировка не включает: \n- производство  питьевой  воды опреснением соленой воды, см. 41.00.1', 174, '14.4', 0);
