INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(180, '14.50.21', 'Добыча драгоценных и полудрагоценных камней, кроме алмазов, самоцветов и янтаря', 'Добыча драгоценных   и   полудрагоценных  камней,  кроме алмазов, самоцветов и янтаря \nЭта группировка включает: \n- извлечение драгоценных камней из коренных, россыпных и техногенных месторождений, а также сортировку, первичную классификацию и первичную  оценку  изумрудов,  сапфиров, александритов, рубинов, а также янтаря', 179, '14.50.2', 0);
