INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(237, '15.61.1', 'Производство обработанного риса', 'Производство обработанного риса \nЭта группировка включает: \n- обработку      риса:     производство     обрушенного, шлифованного,       полированного,       глазированного, пропаренного риса; производство рисовой муки', 236, '15.61', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(238, '15.61.2', 'Производство муки из зерновых и растительных культур и готовых мучных смесей и теста для выпечки', 'Производство муки из зерновых и растительных  культур  и готовых мучных смесей и теста для выпечки \nЭта группировка включает: \n- производство  муки  тонкого  помола  из пшеницы,  ржи, овса, кукурузы и других зерновых культур \n- производство  муки тонкого и грубого помола из сушеных бобовых, корнеплодов и клубней, съедобных орехов \n- производство готовых мучных смесей и теста для выпечки \nЭта группировка не включает: \n- производство   картофельной  муки  тонкого  и  грубого помола, см. 15.31 \n- влажный помол кукурузы, см. 15.62.2', 236, '15.61', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(239, '15.61.3', 'Производство крупы, муки грубого помола, гранул и прочих продуктов из зерновых культур', 'Производство крупы, муки грубого помола, гранул и прочих продуктов из зерновых культур \nЭта группировка включает: \n- производство  муки  грубого помола,  крупы и гранул из пшеницы, ржи, овса, кукурузы и других зерновых культур \n- производство   продуктов   для  завтрака  из  зерновых культур', 236, '15.61', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(240, '15.62', 'Производство кукурузного масла, крахмала и крахмалопродуктов', 'Производство кукурузного     масла,      крахмала      и крахмалопродуктов', 235, '15.6', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(241, '15.62.1', 'Производство кукурузного масла', 'Производство кукурузного масла \nЭта группировка включает: \n- производство  кукурузного  масла  и  его  фракций  без изменения химического состава', 240, '15.62', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(242, '15.62.2', 'Производство крахмала и крахмалопродуктов; производство сахаров и сахарных сиропов, не включенных в другие группировки', 'Производство крахмала и крахмалопродуктов;  производство сахаров  и  сахарных  сиропов,  не  включенных  в другие группировки \nЭта группировка включает: \n- производство крахмала из риса,  картофеля,  кукурузы и т.п. \n- производство  пищевых  продуктов из крахмала (хлопьев, гранул, крупки и т.п.) \n- влажный помол кукурузы \n- производство  глюкозы,  глюкозного  сиропа,  фруктозы, мальтозы и т.п. \n- производство глютена (клейковины), декстринов и прочих модифицированных крахмалов \n- производство инулина \nЭта группировка не включает: \n- производство лактозы, см. 15.51.5 \n- производство свекловичного сахара, см. 15.83', 240, '15.62', 0);
