INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(246, '15.71.2', 'Производство кормового микробиологического белка, премиксов, кормовых витаминов, антибиотиков, аминокислот и ферментов', 'Производство кормового     микробиологического    белка, премиксов, кормовых витаминов, антибиотиков, аминокислот и ферментов \nЭта группировка не включает: \n- производство рыбной муки для корма животных, см. 15.20 \n- производство  жмыха  из  семян масличных культур,  см. 15.41.2', 244, '15.71', 0);
