INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(363, '20.20.1', 'Производство клееной фанеры, щитов, древесных плит и панелей', 'Производство клееной фанеры,  щитов,  древесных  плит  и панелей \nЭта группировка включает: \n- производство клееной фанеры \n- производство фанерных плит \n- производство древесно-стружечных плит \n- производство древесно-волокнистых плит \nЭта группировка не включает: \n- производство       ячеистых     деревянных    панелей, используемых, в основном, для  производства  перегородок и дверей, см. 20.30.1', 362, '20.20', 5);
