INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(370, '20.20.21', 'Производство шпона и листов для клееной фанеры', 'Производство шпона и листов для клееной фанеры \nЭта группировка включает: \n- производство шпона (лущеного, строганого) и листов для клееной фанеры толщиной 6 мм и менее', 369, '20.20.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(371, '20.20.22', 'Производство модифицированной древесины', 'Производство модифицированной древесины \nЭта группировка включает: \n- производство плит, блоков, брусьев или профилированных изделий,    уплотненных   физическим,   химическим   или термическим    способом    с    применением     пропитки термореактивными пластиками или расплавленным металлом', 369, '20.20.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(372, '20.3', 'Производство деревянных строительных конструкций, включая сборные деревянные строения, и столярных изделий', 'Производство деревянных    строительных     конструкций, включая сборные деревянные строения, и столярных изделий', 353, '20', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(373, '20.30', 'Производство деревянных строительных конструкций, включая сборные деревянные строения, и столярных изделий', 'Производство деревянных     строительных    конструкций, включая сборные деревянные строения, и столярных изделий', 372, '20.3', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(374, '20.30.1', 'Производство деревянных строительных конструкций и столярных изделий', 'Производство деревянных   строительных   конструкций   и столярных изделий \nЭта группировка включает: \n- производство  деревянных изделий,  предназначенных для использования, в основном, в строительстве: \n- балок, стропил, несущих элементов покрытий \n- дверей, окон, ставней и рам для них \n- лестниц, перил \n- деревянных     фигурных    профилированных    изделий (плинтусов,   наличников  и  т.п.);  дранки  и  гонта; опалубки для бетонных строительных работ \n- паркетных   досок,  блоков  для покрытия пола и т.п., набранных в панели (щитового паркета) \n- ячеистых    деревянных   панелей,   используемых,   в основном, для производства перегородок и дверей \nЭта группировка не включает: \n- производство ненаборных деревянных покрытий для пола и профилированных пиломатериалов, см. 20.10', 373, '20.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(375, '20.30.2', 'Производство сборных деревянных строений', 'Производство сборных деревянных строений \nЭта группировка включает: \n- производство сборных  деревянных  строений  различного назначения:  жилых  домов,  рабочих бытовок,  хозблоков, бань, контор, гаражей, теплиц, навесов и т.п.', 373, '20.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(376, '20.4', 'Производство деревянной тары', 'Производство деревянной тары', 353, '20', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(377, '20.40', 'Производство деревянной тары', 'Производство деревянной тары \nЭта группировка включает: \n- производство деревянных упаковочных  ящиков,  коробок, решетчатой тары, барабанов и аналогичной деревянной тары и ее частей \n- производство  деревянных поддонов,  стеллажей и прочих деревянных грузовых платформ \n- производство деревянных бочек,  чанов,  кадок и других бондарных изделий и их частей \n- производство деревянных кабельных барабанов \nЭта группировка не включает: \n- производство чемоданов, см. 19.20 \n- производство корзин из материалов  для  плетения,  см. 20.52 \n- производство  тары,  специально   спроектированной   и приспособленной  для  перевозки  одним  или  несколькими видами транспорта (деревянных контейнеров), см. 34.20', 376, '20.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(378, '20.5', 'Производство прочих изделий из дерева и пробки, соломки и материалов для плетения', 'Производство прочих изделий из дерева и пробки,  соломки и материалов для плетения', 353, '20', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(379, '20.51', 'Производство прочих изделий из дерева', 'Производство прочих изделий из дерева \nЭта группировка не включает: \n- производство чемоданов, см. 19.20 \n- производство деревянной обуви, см. 19.30 \n- производство светильников  и  осветительной  арматуры, см. 31.50 \n- производство корпусов часов, см. 33.50.2 \n- производство мебели, см. 36.1 \n- производство деревянных игрушек, см. 36.50 \n- производство бижутерии, см. 36.61 \n- производство метел и щеток, см. 36.62 \n- производство тростей и ручек для зонтов, см. 36.63.3 \n- производство спичек, см. 36.63.6', 378, '20.5', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(380, '20.51.1', 'Производство деревянных инструментов, корпусов и рукояток инструментов, рукояток щеток и метелок, обувных колодок и растяжек для обуви', 'Производство деревянных   инструментов,    корпусов    и рукояток инструментов, рукояток щеток и метелок, обувных колодок и растяжек для обуви', 379, '20.51', 0);
