INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(382, '20.51.3', 'Производство деревянных статуэток и украшений из дерева, мозаики и инкрустированного дерева, шкатулок, футляров для ювелирных изделий или ножей', 'Производство деревянных статуэток и украшений из дерева, мозаики и инкрустированного дерева,  шкатулок,  футляров для ювелирных изделий или ножей', 379, '20.51', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(383, '20.51.4', 'Производство деревянных рам для картин, фотографий, зеркал или аналогичных предметов и прочих изделий из дерева', 'Производство  деревянных  рам  для  картин,  фотографий, зеркал или аналогичных предметов и  прочих  изделий  из дерева \nЭта группировка также включает: \n- производство шпулей, бобин и т.п. \n- производство деревянных гробов', 379, '20.51', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(384, '20.52', 'Производство изделий из пробки, соломки и материалов для плетения', 'Производство изделий из пробки, соломки и материалов для плетения \nЭта группировка включает: \n- обработку натуральной пробки, в том числе производство измельченной,   гранулированной   (агломерированной)   и молотой пробки \n- производство изделий из натуральной пробки \n- производство изделий из агломерированной пробки \n- производство изделий из соломки  и  прочих  материалов для плетения: матов, циновок, ширм и т.п. \n- производство корзин,  коробов, сумок и других плетеных изделий \nЭта группировка не включает: \n- производство   матов   или   циновок   из  текстильных материалов, см. 17.51 \n- производство головных уборов из соломки, см. 18.24.4 \n- производство обуви из  материалов  для  плетения,  см. 19.30 \n- производство  транспортных  средств   и   кузовов   из материалов для плетения, см. 34, 35 \n- производство плетеной мебели, см. 36.1', 378, '20.5', 0);
