INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(408, '22.14', 'Издание звукозаписей', 'Издание звукозаписей \nЭта группировка включает: \n- издание   музыкальных   и   других   звукозаписей   на грампластинках, компакт-дисках, цифровых  видеодисках  и магнитных лентах \n- издание нот, в том числе для слепых \nЭта группировка не включает: \n- производство магнитных носителей с  записью  программ, см. 72.20', 402, '22.1', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(409, '22.14.1', 'Издание музыкальных и других звукозаписей на грампластинках, компакт-дисках, видеодисках и магнитных лентах', 'Издание   музыкальных    и    других   звукозаписей   на грампластинках, компакт-дисках, видеодисках  и магнитных лентах', 408, '22.14', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(410, '22.14.2', 'Издание нот, в том числе для слепых', 'Издание нот, в том числе для слепых', 408, '22.14', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(411, '22.15', 'Прочие виды издательской деятельности', 'Прочие виды издательской деятельности \nЭта группировка включает: \n- издание  фотографий;  эстампов;  плакатов;  календарей всех видов;  иллюстрированных,  поздравительных почтовых открыток;    художественных    репродукций;   переводных картинок и прочих печатных материалов \nЭта группировка не включает: \n- производство записных книжек, блокнотов, ежедневников, см. 22.22', 402, '22.1', 0);
