INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(503, '26.15.2', 'Производство блоков для мощения, стеклоблоков, плит и прочих изделий из прессованного или отформованного стекла, используемых в строительстве; производство стекла для витражей; производство многоячеистого стекла или пеностекла в блоках, плитах и аналогичных формах', 'Производство блоков  для мощения,  стеклоблоков,  плит и прочих  изделий  из  прессованного  или   отформованного стекла,   используемых   в  строительстве;  производство стекла для витражей;  производство многоячеистого стекла или пеностекла в блоках, плитах и аналогичных формах', 501, '26.15', 0);
