INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(541, '26.70.3', 'Производство гранул и порошков из природного камня', 'Производство гранул и порошков из природного камня', 538, '26.70', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(542, '26.8', 'Производство прочей неметаллической минеральной продукции', 'Производство прочей      неметаллической     минеральной продукции', 495, '26', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(543, '26.81', 'Производство абразивных изделий', 'Производство абразивных изделий \nЭта группировка включает: \n- производство жерновов, точильных и шлифовальных камней и изделий  из  природных  или  искусственных  абразивных материалов,    в    том    числе   из   агломерированных искусственных или природных алмазов,  а также изделий из абразивных материалов на мягкой основе \nЭта группировка не включает: \n- производство  пил,  буров и аналогичных инструментов с алмазным покрытием, см. 28.62 \n- производство искусственного корунда, см. 26.82.5 \n- производство   боров   и   дисков,    применяемых    в стоматологии, см. 33.10.1', 542, '26.8', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(544, '26.82', 'Производство прочей неметаллической минеральной продукции, не включенной в другие группировки', 'Производство прочей      неметаллической     минеральной продукции, не включенной в другие группировки', 542, '26.8', 6);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(545, '26.82.1', 'Производство обработанных асбестовых волокон, смесей на основе асбеста и изделий из них', 'Производство обработанных асбестовых волокон,  смесей на основе асбеста и изделий из них \nЭта группировка также включает: \n- производство  пряжи  из  асбестовых  волокон,  тканей, одежды, обуви, головных уборов и др. \n- производство фрикционных материалов для тормозов, муфт и  прочих   аналогичных   изделий   в   несмонтированном состоянии \nЭта группировка не включает: \n- производство изделий из асбестоцемента, см. 26.65', 544, '26.82', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(546, '26.82.2', 'Производство изделий из асфальта или аналогичных материалов', 'Производство изделий   из   асфальта   или   аналогичных материалов \nЭта группировка включает: \n- производство   изделий   из  природного  асфальта  или битума,   каменноугольного   пека,   нефтяного   битума, битумных смесей в виде: \n- прессованных  или формованных пластин, брикетов, плит и плиток,  используемых  для  облицовки,   покрытий  и мощения \n- мягких  (рулонных)   кровельных  и  гидроизоляционных материалов,  состоящих     из     основы     (картона, стекловолокна,  стеклохолста,  искусственных  полотен, джута или алюминиевой фольги),  полностью  или  с двух сторон    покрытой    слоем   асфальта,   битума   или аналогичным материалом \n- литых или формованных труб и контейнеров \nЭта группировка не включает: \n- производство  бумаги  и  картона,  просто  пропитанных битумом, гудроном или аналогичным материалом (содержание пропитывающего   материала   менее  50%  по  массе)  для использования     в     качестве     упаковочных     или гидроизолирующих материалов   (например,  толь  \n-  кожа, пергамин), см. 21.12 \n- производство  тканей,  в  том  числе из стекловолокна, покрытых   или   пропитанных,   например   битумом   или асфальтом, см. 17.54.3, 26.14 \n- производство асфальта,  битума,  пека  в  блоках,  см. 14.50.1, 23.20, 24.14.2', 544, '26.82', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(547, '26.82.3', 'Производство битуминозных смесей на основе природного асфальта или битума, нефтяного битума, минеральных смол или их пеков', 'Производство битуминозных  смесей  на  основе природного асфальта или битума,  нефтяного битума, минеральных смол или  их  пеков,  используемых в дорожном строительстве и для изоляционных покрытий', 544, '26.82', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(548, '26.82.4', 'Производство искусственного графита, коллоидного или полуколлоидного графита, продуктов на основе графита или прочих форм углерода в виде полуфабрикатов', 'Производство искусственного   графита,  коллоидного  или полуколлоидного графита, продуктов на основе графита или прочих форм углерода в виде полуфабрикатов', 544, '26.82', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(549, '26.82.5', 'Производство искусственного корунда', 'Производство искусственного корунда', 544, '26.82', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(550, '26.82.6', 'Производство минеральных теплои звукоизоляционных материалов и изделий', 'Производство минеральных   тепло\n-   и  звукоизоляционных материалов и изделий \nЭта группировка включает: \n- производство  тепло\n-  и  звукоизоляционных материалов: вспененных (вспученных) вермикулита, перлита, керамзита, глины   и  т.п.,  а  также  их  смесей,  используемых  в строительстве (пористые заполнители) \n- производство   теплоизоляционных  изделий,  в  которых стекловата не является преобладающей по объему \n- производство  обработанной  слюды  и изделий из слюды, торфа,   графита,   кроме   графита,   используемого   в электротехнике, и т.п. \nЭта группировка не включает: \n- производство стекловаты и изделий из стекловаты, в том числе    теплоизоляционных   изделий   с   преобладанием стекловаты по объему, см. 26.14 \n- производство  огнеупорных изделий,  обожженных подобно керамике, на основе углеродистых веществ (графита, угля, кокса и т.д.) и глины, см. 26.26 \n- производство блоков,  пластин и аналогичных изделий из искусственного    графита    или   прочих   углеродистых материалов,  применяемых,  в основном,  в электрощетках, см. 26.82.4 \n- производство  угольных  электродов,  щеток  и   прочих частей и деталей для использования в электротехнике, см. 31.62.1', 544, '26.82', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(551, 'Подраздел DJ', 'МЕТАЛЛУРГИЧЕСКОЕ ПРОИЗВОДСТВО И ПРОИЗВОДСТВО ГОТОВЫХ МЕТАЛЛИЧЕСКИХ ИЗДЕЛИЙ', 'МЕТАЛЛИЧЕСКИХ ИЗДЕЛИЙ', 189, 'РАЗДЕЛ D', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(552, '27', 'Металлургическое производство', 'Металлургическое производство Этот  класс   включает   деятельность  по  плавке  и/или рафинированию  черных   и   цветных  металлов  из  руды, чушек   или    лома   с      использованием      методов электрометаллургии   и      прочих      металлургических процессов. Продукция  плавки  и  рафинирования,   обычно в    форме    слитков,    используется   для   прокатки, волочения  и   прессования   при   производстве   листа, полосы, сортового проката,  прутков  или  проволоки  или в  жидкой   форме  для  производства  отливок  и  прочей металлопродукции.   Этот   подраздел    также   включает производство сплавов  металлов  и  сверхпрочных  сплавов путем добавления прочих химических  элементов  в  чистые металлы', 551, 'Подраздел DJ', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(553, '27.1', 'Производство чугуна, стали и ферросплавов', 'Производство чугуна, стали и ферросплавов', 552, '27', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(554, '27.10', 'Производство чугуна, стали и ферросплавов', 'Производство чугуна, стали и ферросплавов \nЭта группировка включает: \n- производство  передельного   и  зеркального  чугуна  в чушках, болванках или прочих первичных формах \n- производство ферросплавов \n- производство полуфабрикатов из чугуна и нелегированной стали \n- производство  слитков,   прочих   первичных   форм   и полуфабрикатов  из   нержавеющей   стали    или   прочей легированной стали \n- производство уголков, фасонных и специальных  профилей из нержавеющей стали или прочей легированной стали \n- производство прутков из нержавеющей  стали  или прочей легированной стали \n- производство   плоского    проката    из    чугуна   и нелегированной стали \n- производство уголков, фасонных и специальных  профилей из чугуна и нелегированной стали \n- производство прутков из чугуна и нелегированной стали \n- производство шпунтовых свай \n- производство материалов для железнодорожных путей', 553, '27.1', 8);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(555, '27.10.1', 'Производство чугуна и доменных ферросплавов', 'Производство чугуна и доменных ферросплавов \nЭта группировка включает: \n- производство  передельного,  литейного  и  зеркального чугунов, доменного ферромарганца и синтикома (материала, получаемого   в    доменных   цехах    путем     добавки в  передельный   чугун   при   его  разливке  на  машине определенных количеств окисленных железорудных окатышей) \nЭта группировка не включает: \n- производство чугуна в вагранках и  электропечах (путем переплава  чугуна   и   металлоотходов   с   применением ферросплавов и лигатур  для легирования),  используемого для производства чугунных отливок, см. 27.51 \n- производство ферросплавов,  получаемых  термическим  и электротермическим процессами, см. 27.13', 554, '27.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(556, '27.10.2', 'Производство продуктов прямого восстановления железной руды', 'Производство продуктов прямого  восстановления  железной руды \nЭта группировка включает: \n- производство металлизованных  окатышей,  в  том  числе после пассивации, брикетов, губчатого железа  с  высоким содержанием  металлического   железа  путем  переработки окускованного железорудного сырья в  шахтных  печах  или восстановлением мелкой руды в специальных агрегатах', 554, '27.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(557, '27.10.3', 'Производство ферросплавов, кроме доменных', 'Производство ферросплавов, кроме доменных \nЭта группировка включает: \n- производство  ферросплавов  и  лигатур,  получаемых  в электроферросплавных печах и рудно-термических агрегатах \nЭта группировка не включает: \n- производство  доменного   ферромарганца,   получаемого путем восстановления в доменной печи  марганцевой  руды, см. 27.10.1', 554, '27.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(558, '27.10.4', 'Производство стали', 'Производство стали \nЭта группировка включает: \n- производство  стали   путем  переработки  передельного чугуна и лома черных металлов в кислородных конвертерах, электропечах,  мартеновских  печах,  включая  двухванные агрегаты,     бессемеровских   конвертерах,   а    также дуплекс-процессом. Сталь поставляют в виде  слитков  для дальнейшей   деформации   в   прокатных   и  (кузнечных) производствах,  а  также в жидком виде для использования в сталелитейных производствах', 554, '27.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(559, '27.10.5', 'Производство полуфабрикатов (заготовок) для переката', 'Производство полуфабрикатов (заготовок) для переката \nЭта группировка включает: \n- производство непрерывно-литой заготовки на  установках непрерывной и полунепрерывной  разливки  стали,  слябов, блюмов, сутунки и заготовок, полученных  путем  прокатки стальных  слитков  на  обжимных  и  заготовочных  станах (слябингах, блюмингах), а также путем  обжатия  стальных слитков на ковочных машинах, прессах и молотах \nЭта группировка не включает: \n- производство трубной заготовки,  полученной  прокаткой на крупносортных и специальных трубозаготовочных станах, см. 27.16', 554, '27.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(560, '27.10.6', 'Производство стального проката горячекатаного и кованого', 'Производство стального проката горячекатаного и кованого', 554, '27.10', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(561, '27.10.61', 'Производство стального сортового проката горячекатаного и кованого', 'Производство стального сортового проката  горячекатаного и кованого \nЭта группировка включает: \n- производство  горячекатаного   и   кованого  сортового (длинномерного) проката из литой  и  катаной  заготовки, а также из стальных слитков нелегированной, легированной и нержавеющей стали, в том числе рельсов, черновых колес и  бандажей,    получаемых    горячей     прокаткой   на колесопрокатных и бандажных станах', 560, '27.10.6', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(562, '27.10.62', 'Производство рельсов', 'Производство рельсов', 560, '27.10.6', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(563, '27.10.63', 'Производство сортового нержавеющего проката', 'Производство сортового нержавеющего проката', 560, '27.10.6', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(564, '27.10.64', 'Производство стального горячекатаного листового (плоского) проката', 'Производство   стального     горячекатаного    листового (плоского) проката \nЭта группировка включает: \n- производство  горячекатаного   листового    (плоского) проката из  литой  и  катаной  заготовки,  а  также   из стальных   слитков   нелегированной,    легированной   и нержавеющей стали', 560, '27.10.6', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(565, '27.10.7', 'Производство холоднокатаного плоского проката без защитных покрытий и с защитными покрытиями', 'Производство  холоднокатаного   плоского   проката   без защитных покрытий и с защитными покрытиями \nЭта группировка включает: \n- производство   тонколистового  проката  шириной  более 500  мм   на   станах  холодной  прокатки  без  защитных антикоррозионных    покрытий     или     с     защитными антикоррозионными  покрытиями;  технологический  процесс включает  травление,  прокатку,  термическую  обработку, смотку в рулон или резку (роспуск) \nЭта группировка не включает: \n- производство узкой холоднокатаной  ленты  (шириной  не более 500 мм), см. 27.32', 554, '27.10', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(566, '27.10.71', 'Производство холоднокатаного листового (плоского) проката без защитных покрытий', 'Производство   холоднокатаного   листового    (плоского) проката без защитных покрытий', 565, '27.10.7', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(567, '27.10.72', 'Производство холоднокатаного плоского проката с защитными покрытиями', 'Производство   холоднокатаного   плоского   проката    с защитными покрытиями', 565, '27.10.7', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(568, '27.10.73', 'Производство холоднокатаного листового (плоского) нержавеющего проката', 'Производство   холоднокатаного    листового   (плоского) нержавеющего проката', 565, '27.10.7', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(569, '27.10.74', 'Производство листовых штрипсов', 'Производство листовых штрипсов', 565, '27.10.7', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(570, '27.10.8', 'Производство железных порошков, прочей металлопродукции из стального проката, не включенной в другие группировки', 'Производство     железных      порошков,          прочей металлопродукции из стального проката, не  включенной  в другие группировки', 554, '27.10', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(571, '27.10.81', 'Производство железных порошков', 'Производство железных порошков \nЭта группировка включает: \n- производство железных  порошков,  в  том  числе  особо чистых, получаемых электролизом или другими  химическими процессами, а также производство гранулированного железа методом распыления чугуна и стали \nЭта группировка не включает: \n- производство изделий из порошков методами  прессования и прокатки, см. 28, 29', 570, '27.10.8', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(572, '27.10.82', 'Производство изделий из стального проката для верхнего строения железнодорожного пути', 'Производство изделий из стального проката  для  верхнего строения железнодорожного пути \nЭта группировка включает: \n- производство катаных и кованых накладок  и  подкладок, токоведущих рельсов  с  деталями  из  цветных  металлов, переводных  рельсов,   крестовин,   переводных  штанг  и других изделий для  верхнего  строения  железнодорожного пути \nЭта группировка не включает: \n- производство рельсов, см. 27.10.61', 570, '27.10.8', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(573, '27.10.83', 'Производство профилей и конструкций шпунтового типа из стального проката', 'Производство профилей и конструкций  шпунтового типа  из стального проката \nЭта группировка включает: \n- производство  профилей   и  строительных   конструкций шпунтового   типа,    получаемых     сваркой   листового (плоского)   и   сортового   проката,   в  том  числе  с предварительной гибкой, а также  производство  шпунтовых конструкций  из  сборных  элементов  (уголков,  балок  и т.п.) путем сварки или клепки', 570, '27.10.8', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(574, '27.10.84', 'Производство холоднотянутого проката, включая подшипниковый', 'Производство     холоднотянутого     проката,    включая подшипниковый', 570, '27.10.8', 0);
