INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(576, '27.21', 'Производство чугунных труб и литых фитингов', 'Производство чугунных труб и литых фитингов \nЭта группировка включает: \n- производство чугунных  труб,  получаемых  как  методом центробежного литья, так и литьем в кокиль \n- производство  литых  фитингов   для  труб   из  чугуна (нековкого и ковкого) и стали', 575, '27.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(577, '27.22', 'Производство стальных труб и фитингов', 'Производство стальных труб и фитингов \nЭта группировка включает: \n- производство  стальных  бесшовных   и   сварных   труб различного диаметра,  а также фитингов,  кроме литых,  и других соединительных частей из стали', 575, '27.2', 6);
