INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(611, '27.31', 'Производство холоднотянутых прутков и профилей', 'Производство холоднотянутых прутков и профилей \nЭта группировка включает: \n- производство   прутков   и   профилей,   в  том  числе высокоточных,  методом  холодной  прокатки  и  холодного волочения   из   сортового   проката   нелегированной  и легированной стали \n- производство  стали  \n-  серебрянки  путем  шлифовки  и зачистки  сортового   холоднотянутого   (калиброванного) проката из легированной и нержавеющей стали', 610, '27.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(612, '27.32', 'Производство холоднокатаных узких полос и лент', 'Производство холоднокатаных узких полос и лент \nЭта группировка включает: \n- производство плоского стального  проката  с  покрытием или  без  покрытия,  в  рулонах  или в виде прямой ленты шириной менее 600 мм, путем вторичной холодной  прокатки горячекатаного плоского проката', 610, '27.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(613, '27.33', 'Производство гнутых стальных профилей', 'Производство гнутых стальных профилей \nЭта группировка включает: \n- производство гнутых профилей на роликогибочных машинах с  перфорацией,  а   также   производство  оцинкованного профилированного настила \nЭта группировка не включает: \n- производство  профилей,  полученных  ручной  гибкой  и сваркой, см. 27.10.83', 610, '27.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(614, '27.34', 'Производство стальной проволоки', 'Производство стальной проволоки \nЭта группировка включает: \n- производство    нелегированной,     легированной     и нержавеющей  проволоки  методом  холодного волочения или протягивания, а также производство плющеной ленты \nЭта группировка не включает: \n- производство   проволоки   для   сварки,    пайки    и металлизации изделий и колючей проволоки, см. 28.73', 610, '27.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(615, '27.4', 'Производство цветных металлов', 'Производство цветных металлов', 552, '27', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(616, '27.41', 'Производство драгоценных металлов', 'Производство драгоценных металлов \nЭта группировка включает: \n- производство   драгоценных   металлов,   добытых    из комплексных  руд,  концентратов  и других полупродуктов, содержащих  драгоценные  металлы,  аффинаж   драгоценных металлов: золота, серебра, платины и т.п. \n- производство сплавов драгоценных металлов \n- нанесение   покрытия   из   драгоценных   металлов  на драгоценные и недрагоценные металлы \n- производство изделий из драгоценных металлов (прутьев, стержней,  профилей,  проволоки, пластин, полос, листов, дисков и т.п.) \n- производство солей и соединений драгоценных металлов \n- производство порошков драгоценных металлов \nЭта группировка не включает: \n- производство  корпусов  часов из драгоценных металлов, см. 33.50.2 \n- производство    ювелирных   изделий   из   драгоценных металлов, см. 36.22.5', 615, '27.4', 0);
