INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(624, '27.42.5', 'Производство полуфабрикатов из алюминия или алюминиевых сплавов', 'Производство полуфабрикатов из алюминия или  алюминиевых сплавов \nЭта группировка включает: \n- производство   полуфабрикатов,   включая  производство упаковочной фольги, из алюминия  и  алюминиевых  сплавов методом   деформации   (прокатка,   прессование,  ковка, штамповка и др.) \nЭта группировка не включает: \n- производство   полуфабрикатов    из     алюминия     и алюминиевых сплавов методом литья (литье по выплавляемым моделям, литье под давлением и т.д.), см. 27.53 \n- производство фольги, используемой  в  полиграфии,  см. 22.25', 617, '27.42', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(625, '27.43', 'Производство свинца, цинка и олова', 'Производство свинца, цинка и олова \nЭта группировка включает: \n- производство свинца, цинка и олова из руды \n- производство   свинца,   цинка   и    олова    методом электролитического  рафинирования отходов и лома свинца, цинка и олова \n- производство сплавов свинца, цинка и олова \n- производство полуфабрикатов из свинца,  цинка и  олова или их сплавов', 615, '27.4', 3);
