INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(629, '27.44', 'Производство меди', 'Производство меди \nЭта группировка включает: \n- производство меди из руды \n- производство     меди    методом    электролитического рафинирования медных отходов и лома \n- производство сплавов меди \n- производство полуфабрикатов из меди или медных сплавов \n- производство проволоки, стержней и пластин для плавких предохранителей', 615, '27.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(630, '27.45', 'Производство прочих цветных металлов', 'Производство прочих цветных металлов \nЭта группировка включает: \n- производство хрома, марганца, никеля и т.п. из руд или оксидов \n- производство хрома,  марганца,  никеля и т.п.  методом электролитического или  алюмотермического  рафинирования отходов и лома этих металлов \n- производство сплавов хрома,  марганца, никеля и других металлов \n- производство полуфабрикатов из хрома, марганца, никеля и других металлов \n- производство никелевого камня \n- производство   спеченных   материалов,  необработанных или в виде изделий, не специфицированных в номенклатуре \nЭта группировка не включает: \n- производство    спеченных    материалов,    содержащих расщепляющиеся и радиоактивные вещества, см. 23.30 \n- производство режущих элементов  (пластин,  накладок  и т.п.)   для  металлорежущего  инструмента  из  спеченных материалов,  изготовленных на основе карбидов  металлов, агломерированных путем спекания, см. 28.62 \n- производство кремния электротермическим способом', 615, '27.4', 7);
