INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(693, '29.11.1', 'Производство двигателей, кроме авиационных, ракетных, автомобильных и мотоциклетных', 'Производство  двигателей,  кроме  авиационных, ракетных, автомобильных и мотоциклетных \nЭта группировка включает: \n- производство   поршневых     подвесных      двигателей внутреннего сгорания для лодок и катеров \n- производство судовых двигателей \n- производство железнодорожных двигателей \n- производство двигателей для стационарной и передвижной энергетики, кроме дизель-генераторов \nЭта группировка не включает: \n- производство     тяговых     авиационных,    ракетных, автомобильных и мотоциклетных двигателей,  см.  34.10.1, 35.30.1, 35.41 \n- производство дизель-генераторов, см. 31.10.1', 692, '29.11', 0);
