INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(697, '29.11.23', 'Производство газовых турбин, кроме турбореактивных и турбовинтовых', 'Производство газовых  турбин,  кроме  турбореактивных  и турбовинтовых \nЭта группировка не включает: \n- производство    турбореактивных    и     турбовинтовых двигателей, см. 35.30.12', 694, '29.11.2', 0);
