INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(699, '29.12', 'Производство насосов, компрессоров и гидравлических систем', 'Производство насосов,  компрессоров   и   гидравлических систем', 691, '29.1', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(700, '29.12.1', 'Производство гидравлических и пневматических силовых установок и двигателей', 'Производство гидравлических  и  пневматических   силовых установок и двигателей \nЭта группировка включает: \n- производство гидравлических и  пневматических  силовых установок  и  двигателей  линейного  действия  (гидро\n- и пневмоцилиндров) \n- производство  прочих  гидравлических  и пневматических силовых  установок   и   двигателей,   включая   паровые двигатели,   не   объединенные   с  бойлером,  ветровые, пружинные и гиревые двигатели \nЭта группировка не включает: \n- производство гидродинамических  передач  (гидромуфт  и гидротрансформаторов), см. 29.14.2', 699, '29.12', 0);
