INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(702, '29.12.3', 'Производство воздушных и вакуумных насосов; производство воздушных и газовых компрессоров', 'Производство воздушных и вакуумных насосов; производство воздушных и газовых компрессоров', 699, '29.12', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(703, '29.12.9', 'Предоставление услуг по монтажу, ремонту и техническому обслуживанию насосов и компрессоров', 'Предоставление услуг по монтажу,  ремонту и техническому обслуживанию насосов и компрессоров', 699, '29.12', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(704, '29.13', 'Производство трубопроводной арматуры', 'Производство трубопроводной арматуры \nЭта группировка включает: \n- производство  промышленных  трубопроводных  кранов   и клапанов,   включая  регулирующие,  предохранительные  и впускные клапаны \n- производство водопроводных кранов, клапанов и вентилей \n- производство кранов и клапанов отопительных систем \n- производство    затворов,    задвижек   и   т.п.   для промышленных трубопроводов \n- предоставление   услуг   по   ремонту  и  техническому обслуживанию трубопроводной арматуры \nЭта группировка не включает: \n- производство  кранов  и  клапанов   из   неупрочненной резины, стекла и керамики, см. 25.13.7, 26.15.85, 26.24 \n- производство впускных и выпускных клапанов  двигателей внутреннего сгорания, см. 34.30, 35.30.11', 691, '29.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(705, '29.14', 'Производство подшипников, зубчатых передач, элементов механических передач и приводов', 'Производство подшипников,  зубчатых  передач,  элементов механических передач и приводов', 691, '29.1', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(706, '29.14.1', 'Производство шариковых и роликовых подшипников', 'Производство шариковых и роликовых подшипников', 705, '29.14', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(707, '29.14.2', 'Производство корпусов подшипников и подшипников скольжения, зубчатых колес, зубчатых передач и элементов приводов', 'Производство корпусов    подшипников    и    подшипников скольжения, зубчатых колес, зубчатых передач и элементов приводов \nЭта группировка включает: \n- производство шарнирных цепей из черных металлов \n- производство   передаточных   валов,   в   том   числе кулачковых и коленчатых валов, и кривошипов \n- производство  корпусов   подшипников   и   подшипников скольжения \n- производство зубчатых колес в сборе с валами, зубчатых передач,  шариковых  ходовых  винтов,  коробок передач и прочих редукционных механизмов \n- производство  гидродинамических  передач  (гидромуфт и гидротрансформаторов) \n- производство   фрикционных  вариаторов  и  фрикционных ременных и цепных передач \n- производство маховиков и шкивов, включая полиспасты \n- производство  муфт  и  шарнирных  соединений,  включая универсальные шарниры Гука, карданы \nЭта группировка не включает: \n- производство   прочих   цепей,  кроме  шарнирных,  см. 28.74.2 \n- производство электромагнитных муфт, см. 31.62.1 \n- производство  коробок  передач,  передаточных   валов, муфт,  дифференциалов и т.п. передаточного оборудования, предназначенного  для   применения   исключительно   или главным образом в автомобилях или самолетах,  см. 34.30, 35.30;   однако    производство    внутренних    деталей автомобильных  и  авиационных  двигателей  (коленчатых и распределительных валов) включается в эту группировку', 705, '29.14', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(708, '29.14.9', 'Предоставление услуг по ремонту подшипников', 'Предоставление услуг по ремонту подшипников', 705, '29.14', 0);
