INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(860, '34.10', 'Производство автомобилей', 'Производство автомобилей \nЭта группировка не включает: \n- производство автомобильных аккумуляторов, см. 31.40.2 \n- производство электрооборудования для автомобилей,  см. 31.61 \n- техническое  обслуживание,  ремонт  и переоборудование автомобилей, см. 50.20', 859, '34.1', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(861, '34.10.1', 'Производство двигателей внутреннего сгорания для автомобилей', 'Производство  двигателей   внутреннего    сгорания   для автомобилей \nЭта группировка включает: \n- производство   поршневых     двигателей    внутреннего сгорания с искровым зажиганием  и  поршневых  двигателей внутреннего сгорания  с  воспламенением  от  сжатия  для автомобилей, тракторов, других  самоходных  транспортных средств, кроме средств  железнодорожного,  воздушного  и водного транспорта \n- производство двигателей для мотоциклов', 860, '34.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(862, '34.10.2', 'Производство легковых автомобилей', 'Производство легковых автомобилей \nЭта группировка включает: \n- производство   моторных   транспортных   средств   для перевозки не  более  девяти  человек,  включая  водителя (включая автомобили \n- амфибии) \n- производство спортивных и гоночных автомобилей \n- производство жилых автофургонов \n- производство  специализированных   автомашин:   скорой помощи, автокатафалков \nЭта группировка не включает: \n- производство    транспортных    средств,    специально предназначенных для передвижения по снегу, автомашин для гольфовых площадок и подобных транспортных средств,  см. 34.10.5', 860, '34.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(863, '34.10.3', 'Производство автобусов и троллейбусов', 'Производство автобусов и троллейбусов \nЭта группировка включает: \n- производство   автобусов  (и  других  автомобилей  для перевозки 10 человек  или  более,  включая  водителя)  и троллейбусов', 860, '34.10', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(864, '34.10.31', 'Производство автобусов', 'Производство автобусов', 863, '34.10.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(865, '34.10.32', 'Производство троллейбусов', 'Производство троллейбусов', 863, '34.10.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(866, '34.10.4', 'Производство грузовых автомобилей', 'Производство грузовых автомобилей \nЭта группировка включает: \n- производство грузовых автомобилей: \n- бортовых,   фургонов,   тягачей   для   полуприцепов, самосвалов, кроме внедорожных,  автоцистерн (с насосами или без насосов), рефрижераторных или термоизоляционных грузовиков, автобетоновозов, мусоросборщиков \n- производство    автомобильных   шасси,   оборудованных двигателем и кабиной \n- производство  шахтных  челноковых транспортных средств для  перевозки  угля  или  руды  от  очистных  машин   к конвейерам \n- производство самозагружающихся  транспортных  средств, оборудованных  лебедками,  подъемными  устройствами,  но предназначенных, в основном, для транспортных целей \nЭта группировка не включает: \n- производство автомобилей \n- самосвалов, предназначенных для использования вне дорожной сети, см. 34.10.5 \n- производство автобетоносмесителей, см. 34.10.5 \n- производство автомобилей специального назначения,  см. 34.10.5', 860, '34.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(867, '34.10.5', 'Производство автомобилей специального назначения', 'Производство автомобилей специального назначения \nЭта группировка включает: \n- производство автомобилей \n- самосвалов, предназначенных для использования вне дорожной сети \n- производство    грузовых    автомобилей,    оснащенных подъемными кранами (автокранов) \n- производство  автомобилей,  специально предназначенных для движения  по  снегу;  производство  автомобилей  для гольфовых площадок и аналогичных транспортных средств \n- производство   автомобилей   специального   назначения прочих, не включенных в другие группировки (автобуровых, автобетононасосов,  автобетоносмесителей, автомастерских и автолабораторий,  пожарных автомобилей,  автомобилей \n- электростанций и т.п.)', 860, '34.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(868, '34.2', 'Производство автомобильных кузовов; производство прицепов, полуприцепов и контейнеров, предназначенных для перевозки одним или несколькими видами транспорта', 'Производство   автомобильных    кузовов;    производство прицепов,  полуприцепов  и  контейнеров, предназначенных для перевозки одним или несколькими видами транспорта', 858, '34', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(869, '34.20', 'Производство автомобильных кузовов; производство прицепов, полуприцепов и контейнеров, предназначенных для перевозки одним или несколькими видами транспорта', 'Производство автомобильных     кузовов;     производство прицепов,  полуприцепов и  контейнеров,  предназначенных для перевозки одним или несколькими видами транспорта \nЭта группировка включает: \n- производство кузовов (включая кабины) для  автомобилей и тракторов \n- производство контейнеров, специально предназначенных и оборудованных для перевозки одним или несколькими видами транспорта \n- производство    прицепов    и    полуприцепов,   кроме самозагружающихся  или  саморазгружающихся  прицепов   и полуприцепов для сельского хозяйства \n- производство  тяжелых  грузовиков  с   откидывающимися рамами  и  погружными  наклонными  трапами для перевозки тяжелой техники и оборудования \nЭта группировка не включает: \n- производство самозагружающихся или  саморазгружающихся прицепов  и  полуприцепов  для сельского хозяйства,  см. 29.32.1', 868, '34.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(870, '34.3', 'Производство частей и принадлежностей автомобилей и их двигателей', 'Производство частей и принадлежностей автомобилей  и  их двигателей', 858, '34', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(871, '34.30', 'Производство частей и принадлежностей автомобилей и их двигателей', 'Производство частей и принадлежностей автомобилей  и  их двигателей \nЭта группировка включает: \n- производство   составных   частей    двигателей    для автомобилей, включая производство впускных  и  выпускных клапанов двигателей внутреннего сгорания \n- производство   прочих  частей  и  принадлежностей  для автомобилей   и   тракторов,   не  включенных  в  другие группировки: тормозов, коробок  передач,  осей,  ходовых колес, амортизаторов подвески,  радиаторов,  глушителей, выхлопных труб, катализаторов,  муфт сцеплений,  рулевых колес, рулевых колонок, рулевых приводов \n- производство ремней безопасности  и  прочих  составных частей  и  принадлежностей  кузовов автомобилей: дверей, бамперов и т.п.', 870, '34.3', 0);
