INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(874, '35.11', 'Строительство и ремонт судов', 'Строительство и ремонт судов', 873, '35.1', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(875, '35.11.1', 'Строительство судов', 'Строительство судов \nЭта группировка включает: \n- строительство военных кораблей \n- строительство   судов   для  грузовых  и  пассажирских перевозок:  строительство   круизных   судов,   паромов, танкеров, транспортных, грузовых и пассажирских судов \n- строительство  промысловых   судов   и   прочих  судов специального  назначения:   рыболовных   судов,   судов\n- рыбозаводов   и   прочих   судов   для   обработки     и консервирования рыбных продуктов, буксиров  и  толкачей, земснарядов, плавучих маяков, пожарных катеров, плавучих кранов и прочих судов специального назначения \n- строительство  плавучих   и   погружных  платформ  для бурения и эксплуатации скважин \n- строительство морских плавучих платформ и  конструкций для  подготовки  к  пуску  и  запуска ракет космического назначения \n- строительство прочих плавучих средств: плавучих баков, причалов, кессонов, буев и бакенов \nЭта группировка не включает: \n- производство гребных винтов, см. 28.75.26 \n- производство судовых двигателей, см. 29.11.1 \n- производство   судовых   навигационных   средств,  см. 33.20 \n- производство автомобилей-амфибий, см. 34.10.2 \n- производство надувных лодок и плотов, см. 35.12', 874, '35.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(876, '35.11.9', 'Предоставление услуг по ремонту, техническому обслуживанию судов и переоборудованию судов', 'Предоставление   услуг    по   ремонту,     техническому обслуживанию судов и переоборудованию судов \nЭта группировка не включает: \n- разрезку судов на металлолом, см. 37.10', 874, '35.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(877, '35.12', 'Строительство и ремонт спортивных и туристских судов', 'Строительство и ремонт спортивных и туристских судов', 873, '35.1', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(878, '35.12.1', 'Строительство спортивных и туристских (прогулочных) судов', 'Строительство спортивных  и   туристских   (прогулочных) судов \nЭта группировка включает: \n- строительство спортивных  и  туристских  (прогулочных) судов: парусных  судов со вспомогательным двигателем или без  него,  надувных  прогулочных  и  спортивных  лодок, прочих спортивных и прогулочных лодок, шлюпок, каноэ \nЭта группировка не включает: \n- производство судовых двигателей, см. 29.11.1 \n- производство виндсерферов, см. 36.40', 877, '35.12', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(879, '35.12.9', 'Предоставление услуг по ремонту, техническому обслуживанию и переоборудованию спортивных и туристских (прогулочных) судов', 'Предоставление   услуг   по    ремонту,     техническому обслуживанию   и    переоборудованию     спортивных    и туристских (прогулочных) судов', 877, '35.12', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(880, '35.2', 'Производство железнодорожного подвижного состава (локомотивов, трамвайных моторных вагонов и прочего подвижного состава)', 'Производство железнодорожного     подвижного     состава (локомотивов,  трамвайных  моторных  вагонов  и  прочего подвижного состава)', 872, '35', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(881, '35.20', 'Производство железнодорожного подвижного состава (локомотивов, трамвайных моторных вагонов и прочего подвижного состава)', 'Производство железнодорожного     подвижного     состава (локомотивов,  трамвайных  моторных  вагонов  и  прочего подвижного состава) \nЭта группировка не включает: \n- производство двигателей и турбин, см. 29.11 \n- производство электродвигателей, см. 31.10 \n- производство   электрооборудования  для  сигнализации, обеспечения  безопасности   и   регулирования   движения транспортных средств, см. 31.62', 880, '35.2', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(882, '35.20.1', 'Производство железнодорожных локомотивов', 'Производство железнодорожных локомотивов \nЭта группировка включает: \n- производство электровозов \n- производство тепловозов \n- производство   прочих  железнодорожных  локомотивов  и тендеров локомотивов', 881, '35.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(883, '35.20.2', 'Производство моторных железнодорожных, трамвайных вагонов и вагонов метро, автомотрис и автодрезин, кроме транспортных средств для ремонта и технического обслуживания железнодорожных и трамвайных путей', 'Производство моторных     железнодорожных,    трамвайных вагонов и вагонов метро,  автомотрис и автодрезин, кроме транспортных   средств   для   ремонта   и  технического обслуживания железнодорожных и трамвайных путей', 881, '35.20', 0);
