INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(885, '35.20.31', 'Производство транспортных средств для ремонта и технического обслуживания железнодорожных, трамвайных и прочих путей', 'Производство транспортных   средств   для   ремонта    и технического обслуживания железнодорожных,  трамвайных и прочих путей', 884, '35.20.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(886, '35.20.32', 'Производство несамоходных пассажирских железнодорожных, трамвайных вагонов и вагонов метро, багажных, почтовых и прочих вагонов специального назначения, кроме вагонов, предназначенных для ремонта и технического обслуживания путей', 'Производство несамоходных  пассажирских железнодорожных, трамвайных вагонов и вагонов метро, багажных, почтовых и прочих  вагонов специального назначения,  кроме вагонов, предназначенных для ремонта и технического  обслуживания путей', 884, '35.20.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(887, '35.20.33', 'Производство несамоходных железнодорожных, трамвайных и прочих вагонов для перевозки грузов', 'Производство несамоходных железнодорожных,  трамвайных и прочих вагонов для перевозки грузов', 884, '35.20.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(888, '35.20.4', 'Производство частей железнодорожных локомотивов, трамвайных и прочих моторных вагонов и подвижного состава; производство путевого оборудования и устройств для железнодорожных, трамвайных и прочих путей, механического и электромеханического; оборудования для управления движением', 'Производство частей     железнодорожных     локомотивов, трамвайных   и  прочих  моторных  вагонов  и  подвижного состава;  производство путевого оборудования и устройств для   железнодорожных,   трамвайных   и   прочих  путей, механического и электромеханического;  оборудования  для управления движением \nЭта группировка включает: \n- производство железнодорожного и трамвайного и  прочего путевого    оборудования    (сборных   путевых   блоков, поворотных платформ,  платформенных  буферов,  габаритов нагрузки) \n- производство   механического   и  электромеханического оборудования сигнализации,  обеспечения безопасности или управления  для  железных  дорог,  трамвайных  и  прочих путей,  шоссейных  дорог,   внутренних   водных   путей, парковок, портовых объектов или аэродромов \nЭта группировка не включает: \n- производство рельсов в несобранном виде, см. 27.16.1 \n- производство прочих изделий из стального  проката  для верхнего строения железнодорожного пути, см. 27.35.2 \n- производство шпал, см. 20.10, 26.66 \n- производство   несущих   элементов   контактной   сети (пилонов, порталов и т.п.), см. 26.61, 28.11 \n- производство   дорожных,   железнодорожных   и  других указателей,  не имеющих механических элементов,  которые классифицируются    в    соответствии    с    материалом изготовления, см. 20.51.4, 28.75.27 \n- производство сигнальных фонарей, см. 31.62.1, 31.50', 881, '35.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(889, '35.20.9', 'Предоставление услуг по ремонту, техническому обслуживанию и переделке железнодорожных локомотивов, трамвайных и прочих моторных вагонов и подвижного состава', 'Предоставление услуг     по     ремонту,    техническому обслуживанию и  переделке  железнодорожных  локомотивов, трамвайных   и  прочих  моторных  вагонов  и  подвижного состава', 881, '35.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(890, '35.3', 'Производство летательных аппаратов, включая космические', 'Производство летательных аппаратов, включая космические', 872, '35', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(891, '35.30', 'Производство летательных аппаратов, включая космические', 'Производство летательных аппаратов, включая космические \nЭта группировка также включает: \n- производство  средств  выведения  космических объектов в космическое пространство \nЭта группировка не включает: \n- производство парашютов, см. 17.40 \n- производство  деталей   систем   зажигания   и  прочих электрических частей  двигателей  внутреннего  сгорания, см. 31.61 \n- производство бортовых авиационных  приборов  и  систем воздушной  навигации,  а  также  бортового  электронного оборудования для ракетной  и  космической  техники,  см. 32.20, 33.20', 890, '35.3', 6);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(892, '35.30.1', 'Производство силовых установок и двигателей для летательных аппаратов, включая космические; устройств для ускоренного взлета самолетов, палубных тормозных устройств; наземных тренажеров для летного состава; их частей', 'Производство   силовых   установок   и   двигателей  для летательных  аппаратов,  включая  космические; устройств для  ускоренного  взлета  самолетов,  палубных тормозных устройств; наземных тренажеров для летного  состава;  их частей', 891, '35.30', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(893, '35.30.11', 'Производство двигателей летательных аппаратов с искровым зажиганием и их частей', 'Производство двигателей летательных аппаратов с искровым зажиганием и их частей', 892, '35.30.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(894, '35.30.12', 'Производство турбореактивных и турбовинтовых двигателей и их частей', 'Производство турбореактивных  и турбовинтовых двигателей и их частей', 892, '35.30.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(895, '35.30.13', 'Производство реактивных двигателей, включая ракетные, и их частей', 'Производство  реактивных  двигателей, включая  ракетные, и их частей \nЭта группировка не включает: \n- производство турбореактивных двигателей, см. 35.30.12', 892, '35.30.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(896, '35.30.14', 'Производство устройств для ускоренного взлета самолетов, палубных тормозных устройств и аналогичных устройств', 'Производство устройств для ускоренного взлета самолетов, палубных тормозных устройств и аналогичных устройств \nЭта группировка включает: \n- производство    устройств   для   ускоренного   взлета самолетов и прочих  катапульт,  стартового  оборудования для летательных аппаратов \n- производство   палубных    тормозных    устройств    и аналогичных устройств \n- производство частей устройств для  ускоренного  взлета самолетов,  палубных  тормозных  устройств и аналогичных устройств', 892, '35.30.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(897, '35.30.17', 'Производство наземных тренажеров для летного состава и их частей', 'Производство наземных тренажеров для летного  состава  и их частей', 892, '35.30.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(898, '35.30.2', 'Производство воздушных шаров, дирижаблей, планеров, дельтапланов и прочих безмоторных летательных аппаратов', 'Производство воздушных  шаров,   дирижаблей,   планеров, дельтапланов и прочих безмоторных летательных аппаратов', 891, '35.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(899, '35.30.3', 'Производство вертолетов, самолетов и прочих летательных аппаратов', 'Производство вертолетов,  самолетов и прочих летательных аппаратов', 891, '35.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(900, '35.30.4', 'Производство космических аппаратов, ракет-носителей и прочих космических объектов', 'Производство  космических  аппаратов, ракет-носителей  и прочих космических объектов \nЭта группировка не включает: \n- производство    военных       ракет-носителей      или баллистических ракет, полезный груз которых возвращается на  землю  после  достижения  своего  апогея  и  которые придают полезной нагрузке конечную скорость не выше 7000 км/ч, см. 29.60 \n- производство  ракетных   двигателей   и   двигательных установок  для   ракет   космического   назначения,  см. 35.30.13 \n- производство бортовой  приемопередающей  аппаратуры  и аппаратуры управления, контрольно-измерительных приборов и  контрольно-испытательной  аппаратуры  для  ракетной и космической  техники,  см.  32.20.1,  33.20.2,  33.20.6, 33.20.7', 891, '35.30', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(901, '35.30.41', 'Производство автоматических космических аппаратов', 'Производство автоматических космических аппаратов', 900, '35.30.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(902, '35.30.42', 'Производство пилотируемых и беспилотных космических кораблей и станций, включая орбитальные, межпланетные, многоразового использования и др.', 'Производство  пилотируемых   и  беспилотных  космических кораблей  и  станций, включая орбитальные, межпланетные, многоразового использования и др.', 900, '35.30.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(903, '35.30.43', 'Производство средств выведения космических объектов в космическое пространство', 'Производство средств выведения  космических  объектов  в космическое пространство \nЭта группировка включает: \n- производство   ракет-носителей,    ступеней     ракет\n- носителей,  разгонных    блоков    и   ускорителей   для космических транспортных средств \n- производство   межступенных   и   переходных  отсеков, механизмов   сочленения     и     разделения    ступеней ракет-носителей и беспилотных летательных аппаратов \n- производство  головных  обтекателей, сборочно-защитных блоков и т.п. \n- производство транспортно-пусковых и транспортировочных контейнеров', 900, '35.30.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(904, '35.30.49', 'Производство прочих космических объектов, не включенных в другие группировки', 'Производство прочих космических объектов, не  включенных в другие группировки', 900, '35.30.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(905, '35.30.5', 'Производство прочих частей и принадлежностей летательных аппаратов, включая части космических объектов и средств их выведения в космическое пространство', 'Производство прочих частей и принадлежностей летательных аппаратов, включая части космических объектов и  средств их выведения в космическое пространство \nЭта группировка также включает: \n- производство  основных   узлов:   фюзеляжей,  крыльев, дверей,  шасси,   стабилизаторов    и   рулей,   рычагов управления,  тормозов   и  тормозных  блоков,  топливных баков, приборных щитков и т.п. \nЭта группировка не включает: \n- производство  частей  бортовых  приборов  авиационной, ракетной и космической техники, см. 32.20, 33.20 \n- производство    специализированных    средств   защиты от воздействия  неблагоприятных  факторов  внешней среды обитания, включая скафандры всех типов, см. 33.10.1 \n- производство частей ракетных двигателей, см. 35.30.13', 891, '35.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(906, '35.30.9', 'Предоставление услуг по монтажу, техническому обслуживанию, ремонту и восстановлению летательных аппаратов и двигателей летательных аппаратов, включая составные части ракет космического назначения', 'Предоставление   услуг    по    монтажу,    техническому обслуживанию,  ремонту    и  восстановлению  летательных аппаратов и двигателей  летательных  аппаратов,  включая составные части ракет космического назначения \nЭта группировка также включает: \n- демонтаж  летательных   аппаратов,   включая   изделия ракет  космического назначения, в том числе отработанных частей в районах падения', 891, '35.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(907, '35.4', 'Производство мотоциклов и велосипедов', 'Производство мотоциклов и велосипедов', 872, '35', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(908, '35.41', 'Производство мотоциклов, мопедов и мотоциклетных колясок', 'Производство мотоциклов, мопедов и мотоциклетных колясок \nЭта группировка включает: \n- производство  мотоциклов,   мопедов   и   велосипедов, снабженных вспомогательным двигателем \n- производство мотоциклетных колясок \n- производство частей и принадлежностей мотоциклов \nЭта группировка не включает: \n- производство двигателей для мотоциклов, см. 34.10.1 \n- производство  частей  мотоциклетных  двигателей,   см. 34.30 \n- производство велосипедов, см. 35.42 \n- производство инвалидных колясок, см. 35.43', 907, '35.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(909, '35.42', 'Производство велосипедов', 'Производство велосипедов \nЭта группировка включает: \n- производство   безмоторных   велосипедов   и    других велосипедов, включая трехколесные грузовые велосипеды \nЭта группировка не включает: \n- производство     велосипедов     со    вспомогательным двигателем, см. 35.41 \n- производство детских велосипедов,  кроме двухколесных, см. 36.50', 907, '35.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(910, '35.43', 'Производство инвалидных колясок', 'Производство инвалидных колясок \nЭта группировка включает: \n- производство  инвалидных  колясок,  оснащенных  или не оснащенных  двигателем  или  другими   механизмами   для передвижения \n- производство частей и принадлежностей  для  инвалидных колясок', 907, '35.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(911, '35.5', 'Производство прочих транспортных средств и оборудования, не включенных в другие группировки', 'Производство прочих транспортных средств и оборудования, не включенных в другие группировки', 872, '35', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(912, '35.50', 'Производство прочих транспортных средств и оборудования, не включенных в другие группировки', 'Производство прочих транспортных средств и оборудования, не включенных в другие группировки \nЭта группировка включает: \n- производство тачек, грузовых тележек, ручных тележек и т.п. \n- производство  транспортных   средств,   приводимых   в движение живой тягой', 911, '35.5', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(913, 'Подраздел DN', 'ПРОЧИЕ ПРОИЗВОДСТВА', '', 189, 'РАЗДЕЛ D', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(914, '36', 'Производство мебели и прочей продукции, не включенной в другие группировки', 'Производство мебели и прочей продукции, не включенной  в другие группировки В  классе  36  классифицируется  производство продукции, не включенной в предыдущие классы раздела  D.  В  данной группировке используются обычные  критерии  формирования группировок раздела D. Ремонт продукции, производимой  в группировке 36, обычно  классифицируется  в  группировке 52.7  "Ремонт   бытовых   изделий  и  предметов  личного пользования",   исключение   составляет  ремонт  мебели, музыкальных  инструментов, профессионального спортивного оборудования, автоматического оборудования для  боулинга (кегельбана)  и  аналогичной продукции, классифицируемой в данной группировке', 913, 'Подраздел DN', 6);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(915, '36.1', 'Производство мебели', 'Производство мебели \nЭта группировка также включает: \n- ремонт и реставрацию мебели \n- производство мебели по заказам населения \n- производство частей мебели \nЭта группировка не включает: \n- производство мебели из бетона, цемента, камня, см. 26', 914, '36', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(916, '36.11', 'Производство стульев и другой мебели для сидения', 'Производство стульев и другой мебели для сидения \nЭта группировка включает: \n- производство стульев и другой мебели  для  сидения  из любого материала для офисов, рабочих и жилых помещений \n- производство стульев и другой  мебели  для сидения  из любого материала для театров, кинотеатров и т.п. \n- производство стульев и другой  мебели  для  сидения из любого материала для транспортных средств \n- производство  диванов,  диванов-кроватей  и комплектов мягкой мебели \n- производство садовой мебели для сидения \nЭта группировка также включает: \n- отделку (обивку стульев и другой мебели для сидения  и т.п. отделку) \nЭта группировка не включает: \n- производство          медицинской,      хирургической, стоматологической и ветеринарной мебели, см. 33.10.2', 915, '36.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(917, '36.12', 'Производство мебели для офисов и предприятий торговли', 'Производство мебели для офисов и предприятий торговли \nЭта группировка включает: \n- производство   специальной   мебели   для   магазинов: прилавков, витрин, полок и т.п. \n- производство мебели  для  офисов,  рабочих  помещений, учебных  заведений,  предприятий  общественного питания, бытового обслуживания и т.п. \n- производство мебели для церковных помещений \nЭта группировка не включает: \n- производство     электрических     светильников      и осветительной арматуры, см. 31.50 \n- производство         медицинской,       хирургической, стоматологической и ветеринарной мебели, см. 33.10.2', 915, '36.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(918, '36.13', 'Производство кухонной мебели', 'Производство кухонной мебели \nЭта группировка включает: \n- производство кухонной мебели из любого материала', 915, '36.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(919, '36.14', 'Производство прочей мебели', 'Производство прочей мебели \nЭта группировка включает: \n- производство мебели из любых материалов  для  спальни, столовой,  общей  комнаты  и  других   жилых  помещений, производство садовой мебели, включая плетеную, и т.п. \nЭта группировка также включает: \n- отделку мебели, кроме  стульев  и  другой  мебели  для сидения  (пульверизационную    обработку,   окрашивание, полирование, лакирование и обивку) \nЭта группировка не включает: \n- производство  светильников  и  осветительной арматуры, см. 31.50 \n- производство диванов,  диванов-кроватей  и  комплектов мягкой мебели, см. 36.11 \n- производство садовой мебели для сидения, см. 36.11', 915, '36.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(920, '36.15', 'Производство матрасов', 'Производство матрасов \nЭта группировка включает: \n- производство основ для матрасов \n- производство матрасов: \n- производство   пружинных  или  набивных  матрасов,  а также  матрасов    с    внутренней     основой     из вспомогательного материала \n- производство  необтянутых матрасов из пористой резины или пенопласта \nЭта группировка не включает: \n- производство  подушек,  в  том числе диванных,  пуфов, думок, стеганых одеял и покрывал, см. 17.40 \n- производство надувных матрасов, см. 25.13.7', 915, '36.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(921, '36.2', 'Производство ювелирных изделий, медалей и технических изделий из драгоценных металлов и драгоценных камней; производство монет', 'Производство ювелирных изделий,  медалей  и  технических изделий  из  драгоценных  металлов и драгоценных камней; производство монет', 914, '36', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(922, '36.21', 'Чеканка монет', 'Чеканка монет \nЭта группировка включает: \n- производство  монет, включая  монеты,  используемые  в качестве платежных средств из  драгоценных (благородных) и недрагоценных (неблагородных) металлов', 921, '36.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(923, '36.22', 'Производство ювелирных изделий, медалей и технических изделий из драгоценных металлов и драгоценных камней', 'Производство ювелирных изделий,  медалей  и  технических изделий из драгоценных металлов и драгоценных камней \nЭта группировка также включает: \n- гравирование изделий из драгоценных металлов \n- производство  изделий   из   недрагоценных   металлов, плакированных драгоценными металлами \n- производство изделий из полудрагоценных, поделочных  и синтетических камней \nЭта группировка не включает: \n- производство   изделий  из  недрагоценных  металлов  с гальваническими покрытиями из драгоценных металлов,  см. 28 \n- производство  орденов   и   медалей  из  недрагоценных материалов, см. 36.61', 921, '36.2', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(924, '36.22.1', 'Производство изделий технического назначения из драгоценных металлов', 'Производство изделий    технического    назначения    из драгоценных металлов \nЭта группировка включает: \n- производство технических,  в  том  числе  лабораторных изделий  из  драгоценных  металлов (тиглей,  электродов, катализаторных решеток,  химически стойкой аппаратуры  и т.п.) \nЭта группировка не включает: \n- производство  драгоценных металлов в первичных формах, см. 27.41 \n- производство  отливок  из  драгоценных  металлов,  см. 27.54 \n- производство медицинских инструментов и их частей, см. 33.10.1', 923, '36.22', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(925, '36.22.2', 'Производство изделий технического назначения из драгоценных камней', 'Производство изделий    технического    назначения    из драгоценных камней \nЭта группировка не включает: \n- производство алмазных инструментов, см. 28.62', 923, '36.22', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(926, '36.22.3', 'Обработка алмазов', 'Обработка алмазов \nЭта группировка включает: \n- огранку и иную обработку алмазов, включая технические', 923, '36.22', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(927, '36.22.4', 'Обработка драгоценных, кроме алмазов, полудрагоценных, поделочных и синтетических камней', 'Обработка драгоценных,  кроме алмазов,  полудрагоценных, поделочных и синтетических камней \nЭта группировка включает: \n- огранку  и иную обработку драгоценных,  кроме алмазов, полудрагоценных,  поделочных  и  синтетических   камней, янтаря,  в том числе восстановленных,  включая обработку камней технического назначения', 923, '36.22', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(928, '36.22.5', 'Производство ювелирных изделий, медалей из драгоценных металлов и драгоценных камней', 'Производство  ювелирных  изделий, медалей из драгоценных металлов и драгоценных камней \nЭта группировка включает: \n- производство  ювелирных   изделий  (колец,  браслетов, брошей,  ожерелий   и  т.п.)  из  драгоценных   металлов (золота, серебра, платины и металлов платиновой  группы) или    из    недрагоценных    металлов,    плакированных драгоценными  металлами,  как  без  вставок,  так  и  со вставками из драгоценных камней (бриллиантов, изумрудов, сапфиров, рубинов, александритов), жемчуга,  янтаря,  из полудрагоценных, поделочных  и  синтетических  камней  и других материалов \n- производство ювелирных изделий (ожерелий, браслетов  и т.п.)  из  драгоценных камней, жемчуга, полудрагоценных, поделочных и синтетических камней \n- производство ювелирных изделий бытового  и  культового назначения   (столовых   приборов,   посуды,   туалетных принадлежностей, декоративных изделий для бюро и офисов, ритуально-обрядовых  изделий  и  т.п.)   из  драгоценных металлов; из драгоценных, полудрагоценных, поделочных  и синтетических   камней   и   жемчуга;  из  недрагоценных металлов, плакированных драгоценными металлами, как  без вставок, так  и  со  вставками  из  драгоценных  камней, жемчуга,  янтаря,   из   полудрагоценных,  поделочных  и синтетических камней и других материалов \nЭта группировка также включает: \n- производство ювелирных изделий по заказам населения \n- производство  орденов   и   медалей   из   драгоценных металлов,   недрагоценных     металлов,    плакированных драгоценными  металлами,  драгоценных,  полудрагоценных, поделочных и синтетических камней, жемчуга \nЭта группировка не включает: \n- производство  ювелирных   изделий   из   недрагоценных металлов с  гальваническими  покрытиями  из  драгоценных металлов, см. 28 \n- производство  корпусов часов и металлических браслетов для часов, см. 33.50.2 \n- производство  ювелирных   изделий   из   недрагоценных материалов, включая янтарь, см. 36.61 \n- производство  орденов   и   медалей  из  недрагоценных материалов, см. 36.61 \n- ремонт ювелирных изделий, см. 52.73 \n- производство ювелирных изделий народных художественных промыслов, см. 36.63.8', 923, '36.22', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(929, '36.3', 'Производство музыкальных инструментов', 'Производство музыкальных инструментов', 914, '36', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(930, '36.30', 'Производство музыкальных инструментов', 'Производство музыкальных инструментов \nЭта группировка включает: \n- производство струнных инструментов \n- производство   струнных    клавишных     инструментов, включая автоматические пианино \n- производство клавишных  органов,  включая  фисгармонии и  другие   клавишные   инструменты  с   проскакивающими металлическими язычками \n- производство аккордеонов и  аналогичных  инструментов, в том числе губных гармоник \n- производство духовых музыкальных инструментов \n- производство ударных музыкальных инструментов \n- производство электромузыкальных инструментов \n- производство   музыкальных     шкатулок,     шарманок, каллиоп и т.п. \n- производство   частей  и  принадлежностей  музыкальных инструментов: \n- метрономов, камертонов,  камертонов-дудок,  перфокарт, дисков   и    валиков    для   механических  музыкальных инструментов \nЭта группировка также включает: \n- производство  свистков,   рожков  и  других   звуковых сигнальных устройств, в которых струя  воздуха  подается ртом человека \n- ремонт  и    техническое    обслуживание   музыкальных инструментов, включая установку и ремонт органов \nЭта группировка не включает: \n- издание  и   копирование   звуко\n-  и  видеозаписей  на аудио\n-  и  видеомагнитных  лентах,  компакт-дисках  (CD) и цифровых видеодисках (DVD), см. 22.1, 22.3, 92.11 \n- производство            микрофонов,        усилителей, громкоговорителей, наушников  и  аналогичных  устройств, см. 32.30.4 \n- производство   электропроигрывателей,  магнитофонов  и т.п., см. 32.30.3 \n- производство  игрушечных   музыкальных   инструментов, см. 36.50 \n- настройку пианино и других  музыкальных  инструментов, см. 52.74', 929, '36.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(931, '36.4', 'Производство спортивных товаров', 'Производство спортивных товаров', 914, '36', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(932, '36.40', 'Производство спортивных товаров', 'Производство спортивных товаров \nЭта группировка включает: \n- производство  инвентаря  и  оборудования  для  спорта, спортивных игр  в  закрытых  помещениях  и  на  открытом воздухе: \n- жестких, мягких и надувных мячей \n- ракеток, бит и клюшек \n- лыж, лыжных креплений и палок \n- серферов и виндсерферов \n- инвентаря   для   спортивного   рыболовства,   включая рыболовные сачки \n- инвентаря для охоты, альпинизма и т.п. \n- спортивных  кожаных  перчаток  и  спортивных  головных уборов \n- резервуаров для плавательных и гребных бассейнов \n- коньков, включая роликовые, и т.п. \n- луков и арбалетов \n- инвентаря для  гимнастических  залов,  фитнес-центров, занятий атлетикой \nЭта группировка не включает: \n- производство парусов, см. 17.40 \n- производство спортивной одежды, см. 18 \n- производство шорно-седельных изделий, см. 19.20 \n- производство спортивной обуви, см. 19.30 \n- производство оружия и боеприпасов, см. 29.60 \n- производство спортивных  транспортных  средств,  кроме тобогганов, саней и т.п., см. 34, 35 \n- производство спортивно-туристских судов, см. 35.12 \n- производство  бильярдных  столов  и  оборудования  для кегельбанов, см. 36.50 \n- производство хлыстов  и  кнутов  (для  верховой езды), см. 36.63.3', 931, '36.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(933, '36.5', 'Производство игр и игрушек', 'Производство игр и игрушек', 914, '36', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(934, '36.50', 'Производство игр и игрушек', 'Производство игр и игрушек \nЭта группировка включает: \n- производство кукол, одежды и аксессуаров для кукол \n- производство игрушечных животных \n- производство  игрушек   на   колесах,  предназначенных для  езды   или   катания  детей,  включая  трехколесные велосипеды \n- производство   игрушечных   музыкальных  инструментов, мебели и т.п. \n- производство резиновых игр и игрушек \n- производство  изделий   для   увеселительных   игр  на открытом воздухе, настольных или комнатных игр \n- производство игральных карт \n- производство автоматических и механических  игр,  игр, действующих   при    опускании    монет,    столов     и принадлежностей для  бильярда,  специальных  столов  для казино,  автоматического  оборудования для кегельбанов и т.п. \n- производство электронных игр: видеоигр, шахмат и т.п. \n- производство  миниатюрных    моделей    и  аналогичных изделий,  электрических  поездов, детских конструкторов, строительных наборов и т.п. \n- производство головоломок и т.п. \n- производство прочих игрушек из любых материалов \nЭта группировка не включает: \n- производство велосипедов, см. 35.42 \n- производство изделий для народных гуляний,  карнавалов и других развлечений, см. 36.63.7 \n- производство  осветительных   наборов   (электрических гирлянд) для украшения новогодних елок, см. 31.50 \n- издание программного обеспечения для компьютерных игр, см. 72.21', 933, '36.5', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(935, '36.6', 'Производство различной продукции, не включенной в другие группировки', 'Производство различной продукции, не включенной в другие группировки', 914, '36', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(936, '36.61', 'Производство ювелирных изделий из недрагоценных материалов', 'Производство   ювелирных    изделий    из  недрагоценных материалов \nЭта группировка включает: \n- производство ювелирных  изделий  (бижутерии)  в  части личных украшений из недрагоценных материалов \nЭта группировка также включает: \n- производство  орденов   и  медалей  из   недрагоценных материалов \n- производство ювелирных  изделий  (бижутерии)  в  части личных украшений  из  янтаря  (кроме  ювелирных  изделий из драгоценных металлов со вставками из янтаря) \nЭта группировка не включает: \n- производство ювелирных изделий народных художественных промыслов, см. 36.63.8', 935, '36.6', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(937, '36.62', 'Производство метел и щеток', 'Производство метел и щеток \nЭта группировка включает: \n- производство метел,  кистей и  щеток,  включая  щетки, являющиеся составными частями машин, ручных механических щеток  для  уборки  помещений,  дворов  и  улиц,  швабр, метелок  из перьев,  малярных кистей,  малярных валиков, резиновых скребков, прочих метел, щеток и т.п. \n- производство  туалетных  щеток  и  кистей для ухода за внешностью, зубных щеток, художественных кистей и т.п. \n- производство щеток для одежды и обуви, щеток для ухода за животными', 935, '36.6', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(938, '36.63', 'Производство прочей продукции, не включенной в другие группировки', 'Производство прочей продукции,  не включенной  в  другие группировки', 935, '36.6', 8);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(939, '36.63.1', 'Производство каруселей, качелей, тиров и прочих ярмарочных аттракционов', 'Производство каруселей,   качелей,   тиров   и    прочих ярмарочных аттракционов \nЭта группировка также включает: \n- производство  оборудования  для  передвижных   цирков, зверинцев и театров', 938, '36.63', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(940, '36.63.2', 'Производство пишущих принадлежностей', 'Производство пишущих принадлежностей \nЭта группировка включает: \n- производство ручек и карандашей  всех  видов,  включая чертежные, механических и немеханических \n- производство грифелей для карандашей \n- производство штемпелей для датирования,  запечатывания или  нумерации,  ручных  устройств  для  печатания   или рельефного  тиснения этикеток,  ручных печатных наборов, лент для пишущих  машин,  штемпельных  подушек,  мелков, грифельных досок', 938, '36.63', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(941, '36.63.3', 'Производство зонтов, тростей, пуговиц, кнопок, застежек-молний', 'Производство зонтов,     тростей,    пуговиц,    кнопок, застежек \n- молний \nЭта группировка включает: \n- производство   зонтов  от  солнца  и  дождя,  тростей, тростей \n- стульев, хлыстов, кнутов, пуговиц,  кнопок для одежды, застежек \n- молний и т.п.', 938, '36.63', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(942, '36.63.4', 'Производство линолеума на текстильной основе', 'Производство линолеума на текстильной основе \nЭта группировка также включает: \n- производство  прочих  жестких  напольных  покрытий  на текстильной основе \nЭта группировка не включает: \n- производство безосновного линолеума, см. 25.23', 938, '36.63', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(943, '36.63.5', 'Производство изделий из волоса человека или животных; производство аналогичных изделий из текстильных материалов', 'Производство изделий из волоса  человека  или  животных; производство    аналогичных   изделий   из   текстильных материалов', 938, '36.63', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(944, '36.63.6', 'Производство спичек и зажигалок', 'Производство спичек и зажигалок \nЭта группировка также включает: \n- производство курительных трубок,  мундштуков для сигар и сигарет,  изделий из пирофорных веществ,  производство жидкого  топлива  или  сжиженного  газового  топлива для зажигалок', 938, '36.63', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(945, '36.63.7', 'Производство прочих изделий, не включенных в другие группировки', 'Производство прочих  изделий,  не  включенных  в  другие группировки \nЭта группировка включает: \n- производство  изделий  для  праздников,  карнавалов  и прочих  развлечений,  в  том  числе  принадлежностей для демонстрации фокусов и шуток \n- производство детских колясок \n- производство расчесок,  заколок для волос, зажимов для завивки и прочих аналогичных изделий, пуховок и тампонов для  нанесения  косметики,  пульверизаторов  для  духов, бытовых термосов \n- производство   манекенов   и    прочих    моделей    и приспособлений,   предназначенных  для  демонстрационных целей \n- производство ручных сит и решет \n- производство свечей, фитилей и аналогичных изделий \n- производство искусственных цветов, листьев и фруктов \n- производство   прочих   промышленных    изделий,    не включенных в другие группировки \n- набивку чучел \nЭта группировка не включает: \n- производство фитилей для зажигалок, см. 17.54.3', 938, '36.63', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(946, '36.63.8', 'Производство изделий народных художественных промыслов', 'Производство изделий народных художественных промыслов \nЭта группировка включает: \n- художественную обработку дерева и других  растительных материалов \n- производство художественной керамики \n- художественную обработку металлов \n- производство ювелирных изделий народных художественных промыслов \n- миниатюрную лаковую живопись \n- художественную обработку камня \n- художественную обработку кости и рога \n- производство   строчевышитых      изделий     народных художественных промыслов \n- художественное ручное кружево \n- художественное ручное ткачество \n- художественное ручное вязание \n- художественное ручное ковроткачество и ковроделие \n- художественную ручную роспись и набойку тканей \n- художественную обработку кожи и меха \n- прочие   виды     производств      изделий    народных художественных промыслов', 938, '36.63', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(947, '37', 'Обработка вторичного сырья', 'Обработка вторичного сырья \nЭта группировка включает: \n- обработку отходов,  лома  и  изделий,  бывших  или  не бывших в  употреблении,  с  целью  получения  вторичного сырьевого  материала.   Используются   механические  или химические  процессы   обработки.    С    точки   зрения производимых  продуктов  характерным  является  то,  что исходное   сырье    может    быть    сортированным   или несортированным,    но     обычно    не    пригодно  для дальнейшего  непосредственного    использования,   тогда как выходной продукт, полученный  в  результате  данного вида   деятельности,    годен    для   непосредственного использования в производственных  процессах.  Получаемое вторичное сырье следует рассматривать как  промежуточный продукт  (товар),  имеющий определенную стоимость, но не являющийся конечным новым продуктом \nЭта группировка не включает: \n- производство новых конечных  продуктов  из  вторичного сырья   (как   собственного,   так    и   несобственного изготовления), см. 14 \n- 36 \n- оптовую  торговлю  отходами  и  ломом,  включая  сбор, сортирование,   разделение,    разборку   использованных изделий  (товаров),   таких   как  автомобили,  с  целью получения   деталей    для   повторного   использования, (пере)упаковывание,  хранение   и   доставку,   но   без использования  процессов   существенного  преобразования отходов и лома, см. 50, 51, 52 \n- оптовую  или розничную торговлю бывшими в употреблении товарами, см. 50, 51, 52.50 \n- обработку отходов  не  для  дальнейшего  использования в производственных процессах, а с целью удаления, см. 90', 913, 'Подраздел DN', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(948, '37.1', 'Обработка металлических отходов и лома', 'Обработка металлических отходов и лома', 947, '37', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(949, '37.10', 'Обработка металлических отходов и лома', 'Обработка металлических отходов и лома \nЭта группировка включает: \n- обработку металлических отходов и лома и металлических изделий  с  целью  получения вторичного сырья. Примерами обработки являются: \n- механическое   дробление   металлолома,   такого   как старые   автомобили,   стиральные   машины,   мотоциклы, велосипеды  и  т.п.,  с  последующим   сортированием   и разделением \n- механическая  разрезка   крупных  объектов  из  черных металлов, например железнодорожных вагонов \n- прочие методы механической обработки, такие как  резка и прессование для уменьшения объема \nЭта группировка не включает: \n- производство  металлов   в   виде   нового   конечного продукта  или   новых   металлических   изделий  в  виде конечного продукта из  вторичного  металлического  сырья (как  собственного,  так и несобственного производства), см. 27 или 28 \n- разборку автомобилей, демонтаж оборудования,  разборку компьютеров   с   целью  получения   деталей  повторного использования  и   т.п.,  включая  торговлю   бывшими  в употреблении запасными частями, см. 50, 51, 52 \n- удаление    использованных       изделий,     например холодильников, с уничтожением опасных отходов, см. 90', 948, '37.1', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(950, '37.10.1', 'Обработка отходов и лома черных металлов', 'Обработка отходов и лома черных металлов', 949, '37.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(951, '37.10.2', 'Обработка отходов и лома цветных металлов', 'Обработка отходов и лома цветных металлов', 949, '37.10', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(952, '37.10.21', 'Обработка отходов и лома цветных металлов, кроме драгоценных', 'Обработка отходов   и   лома   цветных  металлов,  кроме драгоценных', 951, '37.10.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(953, '37.10.22', 'Обработка отходов и лома драгоценных металлов', 'Обработка отходов и лома драгоценных металлов \nЭта группировка включает: \n- механическую и химическую  обработку  отходов  и  лома драгоценных   металлов   с   получением   полупродуктов, поступающих для дальнейшей переработки \nЭта группировка также включает: \n- обработку растворов солей драгоценных металлов', 951, '37.10.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(954, '37.2', 'Обработка неметаллических отходов и лома', 'Обработка неметаллических отходов и лома', 947, '37', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(955, '37.20', 'Обработка неметаллических отходов и лома', 'Обработка неметаллических отходов и лома \nЭта группировка включает: \n- обработку   неметаллических   отходов    и    лома   и неметаллических изделий  с  целью  получения  вторичного сырья. Примерами обработки являются: \n- регенерация    резиновых    изделий,     таких     как использованные шины,  с целью получения вторичного сырья \n- сортирование  и   гранулирование  пластмассы  с  целью производства вторичного  сырья  для  изготовления  труб, цветочных  горшков,  поддонов  и  аналогичных  продуктов \n- обработка    (очистка,      плавление,      дробление) пластмассовых и  резиновых  отходов  с  целью  получения гранул \n- восстановление  химических   веществ   из   химических отходов \n- дробление, мойка и сортирование стекла \n- дробление,  очистка  и  сортирование  прочих  отходов, например мусора, образуемого при  разрушении  каких-либо объектов, с целью получения вторичного сырья \n- механическое   измельчение    и    дробление   мусора, образуемого при строительстве  и сносе  зданий  (включая деревянные), асфальта \n- обработка  использованных  пищевых  жиров  и  масел  с целью получения вторичного сырья для производства  корма для домашних или сельскохозяйственных животных \n- обработка прочих пищевых отходов и  остатков  с  целью получения вторичного сырья \nЭта группировка не включает: \n- производство новых конечных  продуктов  из  вторичного сырья   (как   собственного,   так    и   несобственного изготовления),    например     прядение     пряжи     из разволокненного тряпичного сырья, производство  бумажной массы из макулатуры или  восстановление  шин  и покрышек следует   относить    к   соответствующим   группировкам обрабатывающих производств, см. 14 \n- 36 \n- переработку  пищевых   отходов   и  остатков  с  целью производства пищевых продуктов, см. 15 \n- переработку  отходов  с   боен  с  целью  производства кормов для животных, см. 15.7 \n- регенерацию    ядерного     топлива      и   обработку радиоактивных ядерных отходов, см. 23.30 \n- производство компоста, см. 24.15 \n- оптовая торговля неметаллическими  отходами  и  ломом, включая сбор, сортирование, упаковывание,  распределение и т.п., но без использования процессов существенного  их преобразования, см. 51.57 \n- оптовая и розничная торговля  бывшими  в  употреблении товарами, см. 50, 51, 52.50 \n- сжигание, сброс, захоронение и т.п. отходов, см. 90 \n- обработку и удаление радиоактивных отходов, содержащих короткоживущие нуклиды, из больниц и т.п., см. 90 \n- обработку  и   удаление   токсичных   и   загрязненных (зараженных) отходов, см. 90 \n- удаление пищевых отходов, в том числе от  производства напитков, и табачных отходов, см. 90 \n- обработку отходов на заводах компостирования  с  целью их  удаления   и   с   получением   побочного   продукта (компоста), см. 90.02', 954, '37.2', 7);
