INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(963, 'РАЗДЕЛ E', 'ПРОИЗВОДСТВО И РАСПРЕДЕЛЕНИЕ ЭЛЕКТРОЭНЕРГИИ, ГАЗА И ВОДЫ', 'Этот   раздел   включает   деятельность   по   снабжению электроэнергией,  газом,  паром и водой с использованием постоянной  инфраструктуры (сетей) трубопроводов и линий электропередачи.  Размер  сетей  не  имеет существенного значения;  включается  также  электро-,  газо-, тепло\n- и водоснабжение  транспортных  стоянок  и  многоквартирных домов. Производство,  эксплуатация  инфраструктуры  и снабжение конечных потребителей может осуществляться одними и теми же    или    различными    предприятиями.   Предприятия, осуществляющие   электро\n-   и/или  газоснабжение,  и/или теплоснабжение,  и/или водоснабжение, классифицируются в соответствии с их основным видом деятельности', NULL, NULL, 1);
