INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(967, '40.11', 'Производство электроэнергии', 'Производство электроэнергии \nЭта группировка включает: \n- эксплуатацию  генерирующих  предприятий   (мощностей), производящих    электроэнергию,      включая    тепловые электростанции,      гидроэлектростанции,        атомные электростанции    и   электростанции,   работающие    на возобновляемых источниках энергии \nЭта группировка также включает: \n- деятельность по  оперативно-диспетчерскому  управлению технологическими процессами на электростанциях', 966, '40.1', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(968, '40.11.1', 'Производство электроэнергии тепловыми электростанциями', 'Производство электроэнергии тепловыми электростанциями \nЭта группировка включает: \n- производство      электроэнергии       паротурбинными, газотурбинными, дизельными и  прочими  электростанциями, работающими на твердом, жидком и газообразном топливе', 967, '40.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(969, '40.11.2', 'Производство электроэнергии гидроэлектростанциями', 'Производство электроэнергии гидроэлектростанциями \nЭта группировка включает: \n- производство   электроэнергии   гидроэлектростанциями, включая гидроаккумулирующие и  приливные электростанции', 967, '40.11', 0);
