INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(971, '40.11.4', 'Производство электроэнергии прочими электростанциями и промышленными блок-станциями', 'Производство электроэнергии прочими  электростанциями  и промышленными блок-станциями \nЭта группировка включает: \n- производство     электроэнергии    ветроэлектрическими станциями,   солнечными,   геотермальными    и   прочими электростанциями и промышленными блок-станциями', 967, '40.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(972, '40.11.5', 'Деятельность по обеспечению работоспособности электростанций', 'Деятельность    по      обеспечению    работоспособности электростанций \nЭта группировка включает: \n- монтаж, наладку,  ремонт  и  техническое  обслуживание электротехнического  и  теплотехнического  оборудования, аппаратуры и средств защиты электростанций', 967, '40.11', 4);
