INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(977, '40.12', 'Передача электроэнергии', 'Передача электроэнергии \nЭта группировка включает: \n- эксплуатацию  передающих   систем,   которые  передают электроэнергию от генерирующих предприятий (мощностей) к распределительным системам', 966, '40.1', 0);
