INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(979, '40.13.1', 'Распределение электроэнергии', 'Распределение электроэнергии \nЭта группировка включает: \n- распределение электроэнергии по  электрическим  сетям, которые доводят  до  конечных  потребителей  (населения, промышленных   предприятий   и   т.п.)   электроэнергию, поступающую от  генерирующих  мощностей  или  передающих систем', 978, '40.13', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(980, '40.13.2', 'Торговля электроэнергией', 'Торговля электроэнергией \nЭта группировка включает: \n- продажу электроэнергии потребителям \n- деятельность посредников и агентов, которые организуют сделки  по  продаже  электроэнергии,   передаваемой   по распределительным системам, управляемым другими лицами', 978, '40.13', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(981, '40.13.3', 'Деятельность по обеспечению работоспособности электрических сетей', 'Деятельность    по     обеспечению     работоспособности электрических сетей \nЭта группировка включает: \n- монтаж, наладку,  ремонт  и  техническое  обслуживание электротехнического оборудования, аппаратуры  и  средств защиты электрических сетей', 978, '40.13', 0);
