INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(983, '40.21', 'Производство газа', 'Производство газа \nЭта группировка включает: \n- производство    искусственного      горючего     газа, используемого   для   газоснабжения,   путем   подземной газификации   угля,   производство   сланцевого    газа, генераторного  газа  и аналогичных искусственных горючих газов \n- производство  газообразного   топлива  с  определенной удельной теплотой сгорания из горючего  газа  различного типа (как природного,  так  и искусственного) путем  его очистки, смешивания и других процессов \nЭта группировка не включает: \n- производство коксового газа, см. 23.10 \n- производство нефтепродуктов, см. 23.20 \n- производство промышленных газов, см. 24.11 \n- оптовую торговлю газообразным топливом и продажу  газа в контейнерах (см. 51.12, 51.51, 52.48, 52.6)', 982, '40.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(984, '40.22', 'Распределение газообразного топлива; торговля газообразным топливом, подаваемым по распределительным сетям', 'Распределение    газообразного     топлива;     торговля газообразным топливом, подаваемым  по  распределительным сетям', 982, '40.2', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(985, '40.22.1', 'Распределение газообразного топлива', 'Распределение газообразного топлива \nЭта группировка включает: \n- транспортирование   и    распределение   газообразного топлива  всех   видов    по    распределительным   сетям газоснабжения    среди      потребителей     (населения, промышленных предприятий и т.п.) \nЭта группировка не включает: \n- транспортирование   газа    по  трубопроводам   (кроме распределительных), см. 60.30', 984, '40.22', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(986, '40.22.2', 'Торговля газообразным топливом, подаваемым по распределительным сетям', 'Торговля   газообразным    топливом,    подаваемым    по распределительным сетям \nЭта группировка включает: \n- продажу    потребителям    газа,     подаваемого    по распределительным газовым системам \n- деятельность агентов,  которые  организуют  сделки  по продаже     газового      топлива,     подаваемого    по распределительным системам, управляемым другими лицами \nЭта группировка не включает: \n- оптовую торговлю газообразным топливом и продажу  газа в контейнерах (см. 51.12, 51.51, 52.48, 52.6)', 984, '40.22', 0);
