INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(996, '40.30.3', 'Распределение пара и горячей воды (тепловой энергии)', 'Распределение пара и горячей воды (тепловой энергии) \nЭта группировка включает: \n- распределение пара и горячей воды  по  тепловым  сетям среди потребителей (населения,  промышленных предприятий и т.п.) \n- деятельность по оперативно \n- диспетчерскому управлению технологическими процессами в тепловых сетях', 988, '40.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(997, '40.30.4', 'Деятельность по обеспечению работоспособности котельных', 'Деятельность по обеспечению работоспособности котельных \nЭта группировка включает: \n- монтаж,  наладку,  ремонт  и  техническое обслуживание теплотехнического     и     другого     технологического оборудования, аппаратуры и средств защиты котельных', 988, '40.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(998, '40.30.5', 'Деятельность по обеспечению работоспособности тепловых сетей', 'Деятельность по обеспечению  работоспособности  тепловых сетей \nЭта группировка включает: \n- монтаж,  наладку,  ремонт  и  техническое обслуживание теплотехнического     и     другого     технологического оборудования, аппаратуры и средств защиты тепловых сетей', 988, '40.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(999, '40.30.6', 'Торговля паром и горячей водой (тепловой энергией)', 'Торговля паром и горячей водой (тепловой энергией) \nЭта группировка включает: \n- продажу потребителям пара  и  горячей  воды  (тепловой энергии), подаваемых по распределительным тепловым сетям \n- деятельность агентов,  которые  организуют  сделки  по продаже  пара  и  горячей   воды   (тепловой   энергии), подаваемых   по   распределительным    тепловым   сетям, управляемым другими лицами', 988, '40.30', 0);
