INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1003, '41.00.1', 'Сбор и очистка воды', 'Сбор и очистка воды \nЭта группировка включает: \n- сбор и очистку как питьевой, так и непитьевой воды \nЭта группировка также включает: \n- опреснение  морской воды для производства пресной воды в качестве основного продукта \nЭта группировка не включает: \n- очистку сточных вод в целях предотвращения загрязнения окружающей среды, см. 90.00.1', 1002, '41.00', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1004, '41.00.2', 'Распределение воды', 'Распределение воды \nЭта группировка включает: \n- распределение  воды   среди   различных   потребителей (населения, промышленных предприятий и т.п.) \nЭта группировка не включает: \n- эксплуатацию оросительных систем в сельском хозяйстве, см. 01.41.3', 1002, '41.00', 0);
