INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1009, '45.11', 'Разборка и снос зданий; производство земляных работ', 'Разборка и снос зданий; производство земляных работ', 1008, '45.1', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1010, '45.11.1', 'Разборка и снос зданий, расчистка строительных участков', 'Разборка и снос зданий, расчистка строительных участков \nЭта группировка включает: \n- разборку или снос зданий и сооружений \n- расчистку  строительных  участков,  включая   удаление растительности и т.п. \n- производство взрывных работ', 1009, '45.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1011, '45.11.2', 'Производство земляных работ', 'Производство земляных работ \nЭта группировка включает: \n- выемку, отсыпку и перемещение грунта,  выравнивание  и планировку  строительных  участков,  отрывку  траншей  и котлованов,    производство     земляных     работ   при строительстве   автомобильных   и    железных     дорог, аэродромов и т.п., удаление  верхних  как  загрязненных, так и незагрязненных   слоев    почвы,    восстановление территорий и т.п. \nЭта группировка также включает: \n- осушение строительных участков \n- осушение   сельскохозяйственных  и   лесохозяйственных площадей \n- производство   работ    по   террасированию   склонов, укреплению оврагов и т.п. \nЭта группировка не включает: \n- захоронение отходов, см. 90.00.2 \n- строительство оросительных каналов, см. 45.24.2 \n- предоставление услуг по улучшению  химических  свойств почвы (известкованию, гипсованию и т.п.), см. 01.41 \n- предоставление услуг  по  эксплуатации   ирригационных систем, см. 01.41.3', 1009, '45.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1012, '45.11.3', 'Подготовка участка для горных работ', 'Подготовка участка для горных работ \nЭта группировка включает: \n- производство  вскрышных  работ  и  других   работ   по подготовке участков к добыче полезных ископаемых', 1009, '45.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1013, '45.12', 'Разведочное бурение', 'Разведочное бурение \nЭта группировка включает: \n- разведочное  бурение  для  отбора  проб   грунта   при производстве строительных работ \nЭта группировка также включает: \n- разведочное  бурение   при  проведении  геофизических, геологических и аналогичных исследований \n- бурение геолого-разведочных скважин на  нефть,  газ  и твердые    полезные    ископаемые    (включая  россыпные месторождения), в том числе в  шельфовой  зоне  морей  и океанов \nЭта группировка не включает: \n- бурение эксплуатационных нефтяных и  газовых  скважин, см. 11.20 \n- бурение водяных скважин, см. 45.25.2 \n- проходку шахтных стволов, см. 45.25.2 \n- геофизические,    геологические     и     сейсмические исследования, см. 74.20', 1008, '45.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1014, '45.2', 'Строительство зданий и сооружений', 'Строительство зданий и сооружений', 1007, '45', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1015, '45.21', 'Производство общестроительных работ', 'Производство общестроительных работ \nЭта группировка не включает: \n- предоставление услуг по добыче нефти и газа, см. 11.20 \n- монтаж сборных строительных конструкций  из  элементов собственного изготовления,  кроме бетонных,  см. 20, 26, 28 \n- строительство   стадионов,   плавательных   бассейнов, гимнастических залов, теннисных кортов, полей для гольфа и  других  спортивных  сооружений,  кроме  строительства зданий, см. 45.23.2 \n- монтаж инженерного оборудования зданий, см. 45.3 \n- производство отделочных и завершающих работ в  зданиях и сооружениях, см. 45.4 \n- деятельность  в  области  архитектуры   и   инженерных изысканий, см. 74.20 \n- управление строительными проектами, см. 74.20', 1014, '45.2', 7);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1016, '45.21.1', 'Производство общестроительных работ по возведению зданий', 'Производство общестроительных работ по возведению зданий \nЭта группировка включает: \n- производство  общестроительных   работ  по  возведению зданий всех типов: жилых, торговых,  производственных  и т.п.,  включая   производство   работ   по   расширению, реконструкции,  реставрации   и   восстановлению  зданий \nЭта группировка не включает: \n- строительство спортивных зданий и сооружений, а  также зданий  и  сооружений  в  местах  отдыха,  см.  45.21.6, 45.23.2 \n- строительство   зданий  из  сборных  конструкций,  см. 45.21.7', 1015, '45.21', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1017, '45.21.2', 'Производство общестроительных работ по строительству мостов, надземных автомобильных дорог, тоннелей и подземных дорог', 'Производство общестроительных   работ  по  строительству мостов,  надземных  автомобильных  дорог,   тоннелей   и подземных дорог \nЭта группировка не включает: \n- производство  общестроительных  работ по строительству наземных автомобильных дорог,  железных дорог, взлетно \n- посадочных полос, см. 45.23.1 \n- монтаж  металлических  строительных  конструкций,  см. 45.25.4 \n- строительство гидротехнических сооружений, см. 45.24.2', 1015, '45.21', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1018, '45.21.3', 'Производство общестроительных работ по прокладке магистральных трубопроводов, линий связи и линий электропередачи', 'Производство общестроительных    работ    по   прокладке магистральных  трубопроводов,  линий   связи   и   линий электропередачи \nЭта группировка включает: \n- производство   общестроительных   работ  по  прокладке магистральных   наземных   и    подводных    нефте\n-    и газопроводов,   трубопроводов   систем  водоснабжения  и канализации  и   прочих   магистральных   трубопроводов, дальних    воздушных   и   кабельных   линий   связи   и электропередачи,  в том числе линий электропередачи  для железных дорог (контактных сетей) \nЭта группировка не включает: \n- производство   общестроительных   работ  по  прокладке местных   трубопроводов,    линий    связи    и    линий электропередачи, см. 45.21.4', 1015, '45.21', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1019, '45.21.4', 'Производство общестроительных работ по прокладке местных трубопроводов, линий связи и линий электропередачи, включая взаимосвязанные вспомогательные работы', 'Производство общестроительных   работ    по    прокладке местных    трубопроводов,    линий    связи    и   линий электропередачи, включая взаимосвязанные вспомогательные работы \nЭта группировка включает: \n- производство   общестроительных   работ  по  прокладке местных  трубопроводов  систем  горячего   и   холодного водоснабжения, отопления, канализационных сетей, местных систем газоснабжения  и  прочих  местных  трубопроводов, местных    воздушных   и   кабельных   линий   связи   и электропередачи \n- производство   взаимосвязанных  вспомогательных  работ (строительство   насосных   станций,    трансформаторных подстанций и т.п.) \nЭта группировка также включает: \n- производство   общестроительных   работ  по  прокладке подземных линий кабельного телевидения \nЭта группировка не включает: \n- производство  общестроительных  работ   по   прокладке магистральных трубопроводов, дальних линий связи и линий электропередачи, см. 45.21.3', 1015, '45.21', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1020, '45.21.5', 'Производство общестроительных работ по строительству электростанций и сооружений для горнодобывающей и обрабатывающей промышленности', 'Производство общестроительных   работ  по  строительству электростанций  и  сооружений  для   горнодобывающей   и обрабатывающей промышленности', 1015, '45.21', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1021, '45.21.51', 'Производство общестроительных работ по строительству гидроэлектростанций', 'Производство общестроительных  работ  по   строительству гидроэлектростанций', 1020, '45.21.5', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1022, '45.21.52', 'Производство общестроительных работ по строительству атомных электростанций', 'Производство общестроительных  работ  по   строительству атомных электростанций', 1020, '45.21.5', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1023, '45.21.53', 'Производство общестроительных работ по строительству тепловых и прочих электростанций', 'Производство общестроительных  работ  по   строительству тепловых и прочих электростанций', 1020, '45.21.5', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1024, '45.21.54', 'Производство общестроительных работ по строительству сооружений для горнодобывающей и обрабатывающей промышленности', 'Производство  общестроительных  работ  по  строительству сооружений    для   горнодобывающей   и   обрабатывающей промышленности \nЭта группировка включает: \n- производство общестроительных работ  по  строительству таких сооружений,  как шахты,  плавильные печи, коксовые печи, силосы, градирни и т.п. \nЭта группировка не включает: \n- производство  общестроительных работ по  строительству складов и промышленных зданий, см. 45.21.1 \n- проходку шахтных стволов, см. 45.25.2', 1020, '45.21.5', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1025, '45.21.6', 'Производство общестроительных работ по строительству прочих зданий и сооружений, не включенных в другие группировки', 'Производство общестроительных   работ  по  строительству прочих зданий  и  сооружений,  не  включенных  в  другие группировки \nЭта группировка включает: \n- производство  общестроительных  работ по строительству зданий  стадионов,  гимнастических   залов,   бассейнов, закрытых катков, теннисных кортов и т.п. \n- производство общестроительных работ  по  строительству прочих  зданий  и  сооружений,  не  включенных  в другие группировки \nЭта группировка также включает: \n- производство общестроительных работ  по  строительству водоочистных сооружений \nЭта группировка не включает: \n- строительство  плоскостных  и  прочих  сооружений  для стадионов и мест отдыха,  например спортивных  площадок, беговых дорожек, трамплинов, см. 45.23.2 \n- установку домашних плавательных бассейнов, см. 45.45', 1015, '45.21', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1026, '45.21.7', 'Монтаж зданий и сооружений из сборных конструкций', 'Монтаж зданий и сооружений из сборных конструкций \nЭта группировка включает: \n- монтаж  зданий  и  сооружений  всех  типов  из сборных конструкций \nЭта группировка не включает: \n- монтаж зданий и сооружений  из  сборных  металлических конструкций, см. 45.25.4 \n- монтаж зданий и  сооружений  из  сборных  строительных конструкций  собственного изготовления,  кроме бетонных, см. 20, 26, 28', 1015, '45.21', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1027, '45.22', 'Устройство покрытий зданий и сооружений', 'Устройство покрытий зданий и сооружений \nЭта группировка включает: \n- монтаж несущих элементов покрытий \n- устройство кровли \n- обеспечение  гидроизоляции   зданий    и   сооружений, включая обработку стен для придания им гидрофобности', 1014, '45.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1028, '45.23', 'Строительство дорог, аэродромов и спортивных сооружений', 'Строительство дорог, аэродромов и спортивных сооружений', 1014, '45.2', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1029, '45.23.1', 'Производство общестроительных работ по строительству автомобильных дорог, железных дорог и взлетно посадочных полос аэродромов', 'Производство  общестроительных  работ  по  строительству автомобильных дорог, железных дорог и взлетно-посадочных полос аэродромов \nЭта группировка включает: \n- строительство  автомобильных  дорог,   улиц  и  прочих дорог для транспортных  средств  и  пешеходов,  открытых автомобильных стоянок и т.п. \n- строительство железных дорог \n- строительство взлетно-посадочных полос \n- разметку проезжей части автомобильных дорог и стоянок \n- разметку надземных дорог (эстакад), мостов и тоннелей \n- установку  защитных   дорожных  ограждений,   дорожных знаков и т.п. \nЭта группировка не включает: \n- строительство надземных автодорог, мостов и  тоннелей, подземных дорог, включая метро, см. 45.21.2 \n- производство дорожных работ,  связанных  с  прокладкой трубопроводов и кабелей, см. 45.21.3, 45.21.4 \n- строительство линий электропередачи для железных дорог (контактных сетей), см. 45.21.3', 1028, '45.23', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1030, '45.23.2', 'Строительство спортивных сооружений', 'Строительство спортивных сооружений \nЭта группировка включает: \n- строительство стадионов,  катков, площадок для  гольфа и  прочих  спортивных  сооружений  и сооружений в местах отдыха, кроме строительства зданий \nЭта группировка не включает: \n- строительство зданий стадионов, гимнастических залов и т.п., см. 45.21.6 \n- производство предварительных земляных работ, см. 45.11', 1028, '45.23', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1031, '45.24', 'Строительство водных сооружений', 'Строительство водных сооружений', 1014, '45.2', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1032, '45.24.1', 'Строительство портовых сооружений', 'Строительство портовых сооружений \nЭта группировка включает: \n- строительство морских и  речных  портовых  сооружений, пристаней для спортивно \n- туристских судов и т.п.', 1031, '45.24', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1033, '45.24.2', 'Строительство гидротехнических сооружений', 'Строительство гидротехнических сооружений \nЭта группировка включает: \n- строительство   дамб,    плотин,   каналов,    шлюзов, акведуков, регуляционных,  гидромелиоративных  и  прочих гидротехнических сооружений \nЭта группировка не включает: \n- строительство водопроводных и  канализационных  сетей, см. 45.21.3, 45.21.4 \n- строительство гидроэлектростанций, см. 45.21.51', 1031, '45.24', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1034, '45.24.3', 'Производство дноуглубительных и берегоукрепительных работ', 'Производство дноуглубительных    и   берегоукрепительных работ', 1031, '45.24', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1035, '45.24.4', 'Производство подводных работ, включая водолазные', 'Производство подводных работ, включая водолазные \nЭта группировка не включает: \n- прокладку подводных кабелей, см. 45.21.3, 45.21.4 \n- производство водолазных работ по обслуживанию судов  и подъему затонувших судов, см. 63.22', 1031, '45.24', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1036, '45.25', 'Производство прочих строительных работ', 'Производство прочих строительных работ \nЭта группировка включает: \n- строительную деятельность,  специализированную в одном направлении,   общем   для   различных  видов  зданий  и сооружений,  и  требующую  специальной  квалификации   и оборудования', 1014, '45.2', 6);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1037, '45.25.1', 'Монтаж строительных лесов и подмостей', 'Монтаж строительных лесов и подмостей \nЭта группировка включает: \n- монтаж и  демонтаж  строительных  лесов  и  подмостей, включая арендуемые \nЭта группировка не включает: \n- аренду строительных лесов и подмостей без их монтажа и демонтажа, см. 71.32', 1036, '45.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1038, '45.25.2', 'Строительство фундаментов и бурение водяных скважин', 'Строительство фундаментов и бурение водяных скважин \nЭта группировка включает: \n- производство   работ   по  строительству  фундаментов, включая производство свайных работ \n- строительство водяных скважин \n- проходку шахтных стволов \nЭта группировка не включает: \n- устройство  фундаментов  монолитного  исполнения,  см. 45.25.3 \n- устройство  фундаментов   мостовых   сооружений,   см. 45.21.2', 1036, '45.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1039, '45.25.3', 'Производство бетонных и железобетонных работ', 'Производство бетонных и железобетонных работ \nЭта группировка включает: \n- производство арматурных работ,  требующих  специальной квалификации и оборудования, гибку и сварку арматуры \n- производство опалубочных работ \n- укладку  бетонной  смеси  в  опалубку  и  производство прочих бетонных работ  (устройство  фундаментов,  стоек, перекрытий,   бетонную   подготовку   и  т.п.),  включая уплотнение бетонной смеси \nЭта группировка не включает: \n- производство     железобетонных     элементов      для строительства, см. 26.61 \n- производство товарного бетона, см. 26.63 \n- устройство дорожной одежды, см. 45.23.1 \n- строительство мостов,  подземных автодорог,  туннелей, метро, см. 45.21.2 \n- производство  работ  по   строительству   фундаментов, требующих специальной квалификации, см. 45.25.2 \n- устройство  фундаментов   мостовых   сооружений,   см. 45.21.2', 1036, '45.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1040, '45.25.4', 'Монтаж металлических строительных конструкций', 'Монтаж металлических строительных конструкций \nЭта группировка включает: \n- монтаж    металлических    строительных    конструкций несобственного  изготовления  при строительстве зданий и сооружений всех типов, включая сварочные работы \nЭта группировка не включает: \n- монтаж    металлических    строительных    конструкций собственного изготовления, см. 28', 1036, '45.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1041, '45.25.5', 'Производство каменных работ', 'Производство каменных работ \nЭта группировка включает: \n- производство кирпичных работ \n- производство работ по укладке каменных блоков \n- производство прочих каменных  работ,  включая  мощение дорог \nЭта группировка не включает: \n- производство бетонных работ, см. 45.25.3 \n- облицовку плитами и плитками участков,  прилегающих  к зданиям  и  сооружениям  (дорожек, внутренних двориков и т.п.), см. 45.43', 1036, '45.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1042, '45.25.6', 'Производство прочих строительных работ, требующих специальной квалификации', '\n- предоставление  услуг  по  эксплуатации  ирригационных систем, см. 01.41.3 Производство  прочих   строительных   работ,   требующих специальной квалификации \nЭта группировка включает: \n- строительство дымовых труб \n- строительство промышленных печей \n- сушку помещений \n- производство подъемных работ \n- производство работ,  требующих  специального  допуска, навыков   подъема    на   высоту   и    соответствующего оборудования, например работ на высоте при строительстве высотных объектов \n- производство  прочих  строительных  работ,   требующих специальной  квалификации,   не   включенных   в  другие группировки, например  устройство  каминов,  перемещение зданий, и т.п.', 1036, '45.25', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1043, '45.3', 'Монтаж инженерного оборудования зданий и сооружений', 'Монтаж инженерного оборудования зданий и сооружений', 1007, '45', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1044, '45.31', 'Производство электромонтажных работ', 'Производство электромонтажных работ \nЭта группировка включает: \n- монтаж в зданиях и других строительных объектах: \n- электропроводов,  электроарматуры   и  электроприборов \n- электропроводок   для    телекоммуникационных   систем (систем электросвязи) \n- систем электрического отопления, включая электрические солнечные батареи (коллекторы) \n- радио\n- и телевизионных антенн \n- систем пожарной сигнализации \n- систем противовзломной (охранной) сигнализации \n- лифтов и эскалаторов \n- молниеотводов и т.п. \nЭта группировка не включает: \n- монтаж  электрооборудования  на  электростанциях,  см. 40.10.4 \n- монтаж испытательного оборудования, см. 33.20.9 \n- ремонт   и   техническое    обслуживание    лифтов   и эскалаторов, см. 29.22.9 \n- монтаж    телекоммуникационных      систем     (систем электросвязи), см. 32.20', 1043, '45.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1045, '45.32', 'Производство изоляционных работ', 'Производство изоляционных работ \nЭта группировка включает: \n- производство тепло-,  звуко\n- и виброизоляционных работ в зданиях и других строительных объектах \nЭта группировка не включает: \n- обеспечение гидроизоляции, см. 45.22', 1043, '45.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1046, '45.33', 'Производство санитарно-технических работ', 'Производство санитарно-технических работ \nЭта группировка включает: \n- монтаж в зданиях и других строительных объектах: \n- водопроводных   систем    (холодного    и     горячего водоснабжения, пожаротушения), канализационных систем  и санитарно-технического оборудования \n- газовых систем и оборудования, включая системы  подачи различных  газов  и газовых смесей (например кислорода в больницах) \n- отопительных,  вентиляционных,  холодильных  систем  и оборудования, систем кондиционирования воздуха \n- неэлектрических солнечных коллекторов \n- автоматических систем пожаротушения \nЭта группировка также включает: \n- установку  и   техническое   обслуживание    устройств управления системами центрального отопления \n- подсоединение  к   районным   системам    центрального отопления \n- ремонт и техническое обслуживание домовых отопительных котлов малой теплопроизводительности и бойлеров \nЭта группировка не включает: \n- установку систем электрического отопления, см. 45.31 \n- ремонт и техническое обслуживание котлов  центрального отопления высокой теплопроизводительности, установленных в  районных   системах   центрального   отопления  и  на теплоузлах, обслуживающих группу  зданий,  см.  40.30.4, 40.30.5 \n- ремонт  и   техническое  обслуживание  холодильного  и вентиляционного оборудования, см. 29.23.9 \n- чистку дымовых труб, см. 74.70.1', 1043, '45.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1047, '45.34', 'Монтаж прочего инженерного оборудования', 'Монтаж прочего инженерного оборудования \nЭта группировка включает: \n- монтаж   систем   освещения   и   сигнализации     для автомобильных  дорог,   железных   дорог,  аэродромов  и портов, производство прочих электромонтажных  работ,  не включенных в другие группировки \n- монтаж  в   зданиях  и  других  строительных  объектах арматуры   и   оборудования,   не  включенных  в  другие группировки \n- ремонт   и    техническое   обслуживание   инженерного оборудования зданий и сооружений \nЭта группировка также включает: \n- установку  оград,   защитных  ограждений  и  т.п.   из различных материалов во дворах,  вокруг  частных  домов, промышленных предприятий и т.п. \n- установку ставней, навесов и т.п. \nЭта группировка не включает: \n- монтаж систем управления  и  безопасности движения  на железных дорогах, см. 45.23.1', 1043, '45.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1048, '45.4', 'Производство отделочных работ', 'Производство отделочных работ', 1007, '45', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1049, '45.41', 'Производство штукатурных работ', 'Производство штукатурных работ \nЭта группировка включает: \n- производство  внешних и внутренних штукатурных работ в зданиях  и   других   строительных   объектах,   включая установку штукатурных сеток \n- устройство сухой штукатурки с применением  гипсовых  и других облицовочных листов', 1048, '45.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1050, '45.42', 'Производство столярных и плотничных работ', 'Производство столярных и плотничных работ \nЭта группировка включает: \n- установку   изделий   и   конструкций   несобственного изготовления:   дверных  и  оконных  блоков  и  коробок, кухонного оборудования,  лестниц, оборудования магазинов и т.п., из деревянных и других материалов \n- производство работ по внутренней  отделке  (устройству потолков,  обшивке стен деревянными панелями,  установке передвижных перегородок и т.п.) \nЭта группировка не включает: \n- устройство  паркетных  и  прочих  деревянных  покрытий полов, см. 45.43 \n- установку оград и ограждений, см. 45.34 \n- установку   декоративных  металлических  изделий,  см. 45.45', 1048, '45.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1051, '45.43', 'Устройство покрытий полов и облицовка стен', 'Устройство покрытий полов и облицовка стен \nЭта группировка включает: \n- облицовку  внутренних   и   наружных   стен  зданий  и сооружений, полов, участков,  прилегающих  к  зданиям  и сооружениям  (дорожек,   внутренних  двориков  и  т.п.), керамическими,   бетонными   и   каменными   плитами   и плитками для стен и пола, керамическими изразцами \n- устройство  паркетных  и  других  деревянных  покрытий полов \n- настил  ковровых  покрытий,  линолеума,  покрытий   из резины и пластмассы \n- облицовку полов и стен тераццо, мрамором,  гранитом  и т.п. \n- оклеивание стен обоями \nЭта группировка не включает: \n- устройство бетонных полов, см. 45.25.3 \n- обшивку стен деревянными панелями, см. 45.42 \n- мощение дорог, см. 45.25.5 \n- деятельность дизайнеров по  созданию  интерьеров,  см. 74.87', 1048, '45.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1052, '45.44', 'Производство малярных и стекольных работ', 'Производство малярных и стекольных работ', 1048, '45.4', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1053, '45.44.1', 'Производство стекольных работ', 'Производство стекольных работ \nЭта группировка включает: \n- установку стекол, включая зеркальные и т.п. \nЭта группировка не включает: \n- установку оконных блоков, см. 45.42', 1052, '45.44', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1054, '45.44.2', 'Производство малярных работ', 'Производство малярных работ \nЭта группировка включает: \n- окрашивание зданий внутри  и  снаружи  и  производство прочих   аналогичных   работ   (нанесение   декоративных покрытий, лаков и т.п.) \n- окрашивание кровельных покрытий \n- окрашивание инженерных сооружений \n- окрашивание ограждений, решеток и т.п. \nЭта группировка не включает: \n- обеспечение гидроизоляции, см. 45.22 \n- разметку краской проезжей части  дорог,  автомобильных стоянок и т.п., см. 45.23.1', 1052, '45.44', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1055, '45.45', 'Производство прочих отделочных и завершающих работ', 'Производство прочих отделочных и завершающих работ \nЭта группировка включает: \n- установку частных плавательных бассейнов \n- пароструйную,  пескоструйную  и прочую очистку фасадов зданий \n- производство прочих отделочных и завершающих работ, не включенных  в  другие  группировки,  например  установка декоративных   металлических  изделий  (панелей,  сеток, решеток, в том числе на радиаторы) Этот класс не включает: \n- внутреннюю уборку зданий и  сооружений,  кроме  уборки зданий после строительства, см. 74.70.1', 1048, '45.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1056, '45.5', 'Аренда строительных машин и оборудования с оператором', 'Аренда строительных машин и оборудования с оператором', 1007, '45', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1057, '45.50', 'Аренда строительных машин и оборудования с оператором', 'Аренда строительных машин и оборудования с оператором \nЭта группировка включает: \n- аренду  кранов   и   других    строительных   машин  и оборудования с оператором \nЭта группировка не включает: \n- аренду машин и оборудования для строительства и  сноса без оператора, см. 71.32', 1056, '45.5', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1058, 'РАЗДЕЛ G', 'ОПТОВАЯ И РОЗНИЧНАЯ ТОРГОВЛЯ; РЕМОНТ АВТОТРАНСПОРТНЫХ СРЕДСТВ, МОТОЦИКЛОВ, БЫТОВЫХ ИЗДЕЛИЙ И ПРЕДМЕТОВ ЛИЧНОГО ПОЛЬЗОВАНИЯ', 'СРЕДСТВ, МОТОЦИКЛОВ, БЫТОВЫХ ИЗДЕЛИЙ И ПРЕДМЕТОВ ЛИЧНОГО ПОЛЬЗОВАНИЯ Этот   раздел  включает  оптовую  и  розничную  торговлю (перепродажу  без  изменения)  любыми  видами  товаров и предоставление  услуг,  связанных  с  продажей  товаров. Оптовая и розничная торговля являются последними этапами в  распространении  товаров.  В  этот  раздел включаются также  ремонт  автотранспортных  средств  и  установка и ремонт бытовых изделий и предметов личного пользования. Предполагается,   что  продажа  без  изменения  включает обычные   операции,   связанные  с  торговлей,  например сортировку   и   сборку   товаров,   смешивание  товаров (например  вин),  розлив  по бутылкам (с предварительной мойкой  бутылок  или  без нее), упаковывание, разделение крупных    партий    товаров    на    более   мелкие   и переупаковывание  мелкими  упаковками,  хранение  (в том числе  в  замороженном  или охлажденном виде), очистку и сушку  сельскохозяйственных  продуктов,  резку древесно\n- волокнистых   плит  или  металлических  листов  и  т.п., производимые за собственный счет. Оптовая торговля представляет собой перепродажу (продажу без  изменения)  новых или бывших в употреблении товаров розничным    торговцам,    производственным,   торговым, учрежденческим  или  профессиональным  пользователям или другим   оптовым   торговцам;   деятельность  агентов  и товарных  брокеров  по покупке товаров от имени подобных лиц  или  компаний,  а  также  по  продаже  им  товаров. Преобладающими    видами    сделок    являются   сделки, заключаемые    оптовиками,    которые    называют   свою деятельность в зависимости от продаваемых товаров, таких как  оптовые  торговцы,  оптовые торговые фирмы, оптовые предприятия  по  сбыту промышленных товаров, экспортеры, импортеры,    закупочные    кооперативные   объединения, территориально  обособленные  подразделения,  филиалы  и представительства  (но  не магазины розничной торговли), которые  поддерживаются  обрабатывающими или добывающими предприятиями  с  целью организации сбыта их продукции и деятельность  которых не ограничивается только принятием заказов  на  поставку  продукции с этих производственных предприятий. Включается   также   деятельность   товарных   брокеров, комиссионеров,  агентов,  оптовиков-скупщиков,  а  также кооперативных     организаций,    занимающихся    сбытом сельскохозяйственной  продукции. Деятельность по оптовой торговле    часто    сопровождается   сборкой   товаров, сортированием  крупных  партий  товаров,  их делением на более       мелкие       партии,      переупаковыванием, перераспределением товаров небольшими партиями, например фармацевтической   продукции;   хранением,  охлаждением, доставкой    и    установкой    товаров,    участием   в рекламировании   и   продвижении  товаров  и  разработке товарных этикеток. Розничная   торговля   представляет   собой  перепродажу (продажу  без  изменения)  новых и бывших в употреблении товаров,  главным  образом,  широким слоям населения для личного   потребления   или  для  домашнего  пользования магазинами,     универмагами,     палатками,    фирмами, выполняющими      заказы      по     почте,     уличными торговцами-лоточниками  и разносчиками, потребительскими кооперативами, а также на аукционах и т.п. Большинство    розничных    торговцев    называют   свою деятельность  в  зависимости  от  продаваемых  товаров и действуют  от  своего  имени,  однако  некоторые  из них действуют  как  агенты от имени комитента и осуществляют торговые   сделки   на   основании   договора   или   за комиссионное вознаграждение', NULL, NULL, 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1059, 'Подраздел GA', 'ОПТОВАЯ И РОЗНИЧНАЯ ТОРГОВЛЯ; РЕМОНТ АВТОТРАНСПОРТНЫХ СРЕДСТВ, МОТОЦИКЛОВ, БЫТОВЫХ ИЗДЕЛИЙ И ПРЕДМЕТОВ ЛИЧНОГО ПОЛЬЗОВАНИЯ', 'СРЕДСТВ, МОТОЦИКЛОВ, БЫТОВЫХ ИЗДЕЛИЙ И ПРЕДМЕТОВ ЛИЧНОГО ПОЛЬЗОВАНИЯ', 1058, 'РАЗДЕЛ G', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1060, '50', 'Торговля автотранспортными средствами и мотоциклами, их техническое обслуживание и ремонт', 'Торговля автотранспортными средствами и мотоциклами,  их техническое обслуживание и ремонт \nЭта группировка включает: \n- все виды деятельности,  кроме  производства, аренды  и проката,   связанные   с  автотранспортными  средствами, включая     легковые     и     грузовые      автомобили, специализированные грузовые автомобили, автобусы, пикапы и  легковые  фургоны,  жилые  автофургоны,   специальные автомобили,  седельные тягачи,  прицепы,  полуприцепы, а также с мотоциклами: \n- оптовую   и  розничную  торговлю  новыми  и бывшими в употреблении     автотранспортными     средствами     и мотоциклами \n- техническое  обслуживание и  ремонт  автотранспортных средств и мотоциклов \n- оптовую и   розничную  торговлю  деталями,  узлами  и принадлежностями автотранспортных средств и мотоциклов \n- деятельность   агентов,   связанных   с   оптовой   и розничной   торговлей  автотранспортными  средствами  и мотоциклами \n- мойку,   полирование  и  буксировку  автотранспортных средств и мотоциклов \nЭта группировка включает также: \n- розничную  торговлю  моторным   топливом,   смазочными материалами и охлаждающими жидкостями \nЭта группировка не включает: \n- аренду  автотранспортных  средств  без водителей,  см. 71.10, 71.21.1 \n- аренду  автотранспортных  средств  с  водителями,  см. 60.22, 60.24.3 \n- аренду мотоциклов, см. 71.21.1', 1059, 'Подраздел GA', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1061, '50.1', 'Торговля автотранспортными средствами', 'Торговля автотранспортными средствами', 1060, '50', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1062, '50.10', 'Торговля автотранспортными средствами', 'Торговля автотранспортными средствами \nЭта группировка включает: \n- торговлю    новыми    и    бывшими    в   употреблении автотранспортными средствами: \n- легковыми автомобилями, пикапами и легковыми фургонами \n- автобусами и микроавтобусами \n- грузовыми автомобилями, специализированными  грузовыми автомобилями, специальными автомобилями \n- седельными тягачами, прицепами и полуприцепами \n- туристскими  автотранспортными   средствами    (жилыми автофургонами и прицепами) \n- автомобилями-вездеходами (джипами и т.п.) \nЭта группировка также включает: \n- торговлю   автомобилями   на   аукционах   и  торговлю автомобилями в сети Интернет \nЭта группировка не включает: \n- торговлю    автомобильными    деталями,    узлами    и принадлежностями, см. 50.30 \n- торговлю   мотоциклами,   их   деталями,   узлами    и принадлежностями, см. 50.40 \n- торговлю   саморазгружающимися   и  самозагружающимися прицепами,  используемыми  в  сельском   хозяйстве,  см. 51.14.2, 51.66.2', 1061, '50.1', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1063, '50.10.1', 'Оптовая торговля автотранспортными средствами', 'Оптовая торговля автотранспортными средствами \nЭта группировка включает: \n- оптовую торговлю всеми видами автомобилей, автобусами, жилыми  автофургонами,  седельными тягачами,  прицепами, полуприцепами как новыми,  так и бывшими в употреблении, от своего имени (за собственный счет, без посредников)', 1062, '50.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1064, '50.10.2', 'Розничная торговля автотранспортными средствами', 'Розничная торговля автотранспортными средствами \nЭта группировка включает: \n- розничную  торговлю  легковыми  автомобилями,   жилыми автофургонами  и  прицепами как новыми,  так и бывшими в употреблении,  от  собственного  имени  (за  собственный счет, без посредников)', 1062, '50.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1065, '50.10.3', 'Торговля автотранспортными средствами через агентов', 'Торговля автотранспортными средствами через агентов \nЭта группировка включает: \n- оптовую   торговлю   через   агентов   всеми    видами автомобилей,     автобусами,    жилыми    автофургонами, седельными  тягачами,   прицепами,   полуприцепами   как новыми, так и бывшими в употреблении \n- розничную    торговлю    через    агентов    легковыми автомобилями,   жилыми  автофургонами  и  прицепами  как новыми, так и бывшими в употреблении', 1062, '50.10', 0);
