INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1068, '50.20.1', 'Техническое обслуживание и ремонт легковых автомобилей', 'Техническое обслуживание и ремонт легковых автомобилей \nЭта группировка включает: \n- техническое обслуживание, ремонт  электрооборудования, ремонт шин, их установку и замену, включая регулировку и балансировку  колес,  механический  ремонт  (двигателей, карбюраторов,   систем  впрыска  топлива  с  электронным управлением, подвесок, рулевого управления,  тормозов  и т.п.), ремонт кузовов и  их  составных  частей  (дверей, замков, окон), включая  окрашивание,  ремонт  сидений  и т.п.', 1067, '50.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1069, '50.20.2', 'Техническое обслуживание и ремонт прочих автотранспортных средств', 'Техническое    обслуживание     и     ремонт      прочих автотранспортных средств \nЭта группировка включает: \n- техническое обслуживание, ремонт  электрооборудования, ремонт шин, их установку и замену, включая регулировку и балансировку  колес,  механический  ремонт  (двигателей, карбюраторов  с   электронным   управлением,   подвесок, рулевого управления, тормозов и т.п.),  ремонт  кузовов, кабин и их  составных  частей  (дверей,  замков,  окон), включая окрашивание, ремонт сидений и т.п.', 1067, '50.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1070, '50.20.3', 'Предоставление прочих видов услуг по техническому обслуживанию автотранспортных средств', 'Предоставление прочих  видов   услуг   по   техническому обслуживанию автотранспортных средств \nЭта группировка включает: \n- мойку,  полирование, нанесение защитных и декоративных покрытий на кузов,  чистку салона,  буксировку и  прочие аналогичные виды деятельности \n- оказание технической помощи в пути', 1067, '50.20', 0);
