INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1083, '50.50', 'Розничная торговля моторным топливом', 'Розничная торговля моторным топливом \nЭта группировка включает: \n- розничную    торговлю    моторным     топливом     для автотранспортных    средств,    мотоциклов    и    т.п., осуществляемую     стационарными     и      передвижными автозаправочными станциями и т.п. \nЭта группировка также включает: \n- розничную    торговлю    смазочными    материалами   и охлаждающими жидкостями  для  автотранспортных  средств, мотоциклов и т.п. \nЭта группировка не включает: \n- оптовую торговлю топливом, см. 51.12.1, 51.51.2', 1082, '50.5', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1084, '51', 'Оптовая торговля, включая торговлю через агентов, кроме торговли автотранспортными средствами и мотоциклами', 'Оптовая   торговля,  включая  торговлю  через   агентов, кроме  торговли    автотранспортными    средствами     и мотоциклами \nЭта группировка включает: \n- как  внутреннюю,  так  и  внешнюю  торговлю,   включая перепродажу (продажу без изменения) новых или  бывших  в употреблении      товаров      розничным      торговцам, производственным,     торговым,    учрежденческим    или профессиональным   пользователям   или   другим  оптовым торговцам; деятельность агентов по  покупке  товаров  от имени подобных лиц или компаний, а также по  продаже  им товаров:         деятельность      оптовых    торговцев, комиссионеров,   оптовых   фирм  по  сбыту  промышленных товаров,     экспортеров,     импортеров,     закупочных кооперативных объединений, товарных  брокеров,  агентов, оптовиков-скупщиков, а также  кооперативных организаций, занимающихся сбытом сельскохозяйственной продукции \n- группировки  51.2  \n-  51.9  включают  только   оптовую торговлю от  своего  имени  (за  собственный  счет,  без посредников) \nЭта группировка также включает: \n- операции,   связанные  с  оптовой  торговлей:  сборка, сортирование  крупных  партий  товаров,  их  деление  на более  мелкие   партии,  переупаковывание,  фасование  и розлив по бутылкам,  хранение,  охлаждение,  доставка  и установка товаров за собственный счет \n- упаковывание  твердых   товаров  и  розлив  в  бутылки жидких   или    помещение     в    закрытые   контейнеры газообразных товаров, включая смешивание и фильтрование, осуществляемые за свой счет \nЭта группировка не включает: \n- оптовую  торговлю   автотранспортными   средствами   и мотоциклами, см. 50.10.1, 50.10.3, 50.40.1, 50.40.3 \n- оптовую торговлю деталями,  узлами и  принадлежностями для  автотранспортных средств и мотоциклов, см. 50.30.1, 50.30.3, 50.40.1, 50.40.3 \n- упаковывание  твердых   товаров  и  розлив  в  бутылки жидких или помещение в закрытые контейнеры  газообразных товаров,  включая смешивание и фильтрование, для третьей стороны, см. 74.82 \n- торговлю электроэнергией, тепловой энергией  и  газом, подаваемым по распределительным сетям газоснабжения, см. 40 \n- аренду и прокат товаров, см. 71', 1059, 'Подраздел GA', 7);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1085, '51.1', 'Оптовая торговля через агентов (за вознаграждение или на договорной основе)', 'Оптовая торговля через агентов (за  вознаграждение   или на договорной основе) \nЭта группировка включает: \n- деятельность  комиссионеров,   товарных   брокеров   и других  посредников   оптовой  торговли,  осуществляющих сделки от имени или за счет других лиц или фирм \n- деятельность,  связанную   со  сведением  продавцов  с покупателями или с  осуществлением  коммерческих  сделок от имени комитента, в том числе  с  использованием  сети Интернет \nЭта группировка также включает: \n- деятельность  по   оптовой   торговле   на  аукционах, включая оптовую торговлю с использованием сети Интернет \nЭта группировка не включает: \n- оптовую торговлю от своего имени (за собственный счет, без посредников), см. 51.2 \n- 51.7 \n- розничную торговлю через агентов, см. 52 \n- деятельность страховых агентов, см. 67.20.1 \n- деятельность  агентов,   осуществляющих   операции   с недвижимым имуществом, см. 70.3', 1084, '51', 9);
