INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1200, '51.41.1', 'Оптовая торговля текстильными изделиями, кроме текстильных галантерейных изделий', 'Оптовая торговля    текстильными    изделиями,     кроме текстильных галантерейных изделий \nЭта группировка включает: \n- оптовую  торговлю  текстильными  тканями и трикотажным полотном,  предназначенными  для  изготовления   одежды, белья и т.п. \n- оптовую торговлю столовым и постельным бельем \n- оптовую   торговлю   прочими   текстильными  изделиями (канатами, веревками, тентами и т.п.)', 1199, '51.41', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1201, '51.41.2', 'Оптовая торговля галантерейными изделиями', 'Оптовая торговля галантерейными изделиями \nЭта группировка включает: \n- оптовую торговлю кружевами,  лентами, тесьмой, пряжей, нитками и т.п. \n- оптовую торговлю тюлегардинными изделиями \n- оптовую  торговлю  металлическими   и   пластмассовыми галантерейными  изделиями  (иглами,  вязальными спицами, крючками, пуговицами и т.п.) \nЭта группировка не включает: \n- оптовую торговлю текстильными волокнами, см. 51.56.2 \n- оптовую торговлю коврами, см. 51.47.14 \n- оптовую торговлю аксессуарами одежды, см. 51.42.5', 1199, '51.41', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1202, '51.42', 'Оптовая торговля одеждой, включая нательное белье, и обувью', 'Оптовая торговля одеждой,  включая  нательное  белье,  и обувью \nЭта группировка также включает: \n- оптовую торговлю спортивной одеждой \nЭта группировка не включает: \n- оптовую торговлю спортивной обувью, см. 51.47.35 \n- оптовую торговлю ювелирными изделиями и  изделиями  из кожи, см. 51.47.34, 51.47.36', 1198, '51.4', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1203, '51.42.1', 'Оптовая торговля одеждой, кроме нательного белья', 'Оптовая торговля одеждой, кроме нательного белья \nЭта группировка включает: \n- оптовую торговлю мужской,  женской, детской одеждой из всех видов тканей,  трикотажных полотен,  пряжи и нитей, нетканых текстильных материалов,  кожи и  т.п.,  включая форменную, специальную, рабочую, спортивную одежду \nЭта группировка не включает: \n- оптовую торговлю изделиями из меха, см. 51.42.3', 1202, '51.42', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1204, '51.42.2', 'Оптовая торговля нательным бельем', 'Оптовая торговля нательным бельем \nЭта группировка включает: \n- оптовую торговлю нательным мужским, женским и  детским бельем из всех видов тканей, трикотажным бельем \nЭта группировка также включает: \n- оптовую торговлю чулочно \n- носочными изделиями \nЭта группировка не включает: \n- оптовую  торговлю  столовым  и постельным бельем,  см. 51.41.1', 1202, '51.42', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1205, '51.42.3', 'Оптовая торговля изделиями из меха', 'Оптовая торговля изделиями из меха \nЭта группировка включает: \n- оптовую  торговлю  меховыми  шкурками  и  изделиями из натурального меха', 1202, '51.42', 0);
