INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1282, '51.56.5', 'Оптовая торговля прочими промежуточными продуктами, кроме сельскохозяйственных, не включенными в другие группировки', 'Оптовая торговля  прочими   промежуточными   продуктами, кроме  сельскохозяйственных,  не  включенными  в  другие группировки \nЭта группировка включает: \n- оптовую  торговлю  промежуточными  продуктами,   кроме сельскохозяйственных,  например  химическими продуктами, не включенными в другие группировки,  резинотехническими изделиями,   изделиями  из  пластмасс,  неметаллическими минеральными  продуктами,  не   включенными   в   другие группировки', 1278, '51.56', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1283, '51.57', 'Оптовая торговля отходами и ломом', 'Оптовая торговля отходами и ломом \nЭта группировка включает: \n- оптовую  торговлю   (куплю-продажу)  металлическими  и неметаллическими отходами, ломом, утилем  и  материалами с  целью  их  повторного  использования,  включая  сбор, сортирование,   разделение,   разборку    использованных изделий   (товаров),  таких  как  автомобили,  с   целью получения   годных    для     использования     деталей, (пере)упаковывание,   хранение   и   доставку,   но  без применения  процессов   их  существенной обработки.  При этом покупаемые и продаваемые  отходы  имеют  остаточную стоимость \n- разборку отслуживших  транспортных  средств  (разборку автомобилей; оптовую торговлю полученным ломом;  продажу годных  к   употреблению   деталей   частным   лицам   и профессиональным пользователям) \nЭта группировка также включает: \n- сбор стеклотары у населения \nЭта группировка не включает: \n- обработку  отходов,   лома   и   других   предметов  с получением   вторичного   сырья.   Для  этого  требуется применение процессов  реального  преобразования  отходов и  лома.   Получаемое   в   результате  вторичное  сырье пригодно   для   непосредственного    использования    в производственных   процессах   и  не  является  конечным новым продуктом, см. 37.10, 37.20 \n- разрезку  автомобилей  с  использованием  механических процессов, см. 37.10 \n- розничную торговлю бывшими  в  употреблении  товарами, см. 52.50 \n- обработку отходов не для их последующего использования в производственных процессах, а с целью удаления, см. 90 \n- сбор и обработку отходов производства  и  потребления, см. 90', 1243, '51.5', 0);
