INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1297, '51.87.1', 'Оптовая торговля транспортными средствами и оборудованием, кроме автомобилей, мотоциклов и велосипедов', 'Оптовая    торговля     транспортными     средствами   и оборудованием,   кроме   автомобилей,    мотоциклов    и велосипедов \nЭта группировка включает: \n- оптовую  торговлю   транспортными  средствами,   кроме автотранспортных   средств,  мотоциклов  и  велосипедов, например  судами,  железнодорожным  подвижным  составом, летательными   аппаратами,    включая   космические,   и прочими транспортными средствами и оборудованием \nЭта группировка не включает: \n- оптовую  торговлю   автотранспортными  средствами, см. 50.10.1, 50.10.3 \n- оптовую торговлю деталями для  автомобилей,  узлами  и принадлежностями, см. 50.30.1, 50.30.3 \n- оптовую торговлю мотоциклами, см. 50.40.1, 50.40.3 \n- оптовую торговлю велосипедами, см. 51.47.35', 1296, '51.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1298, '51.87.2', 'Оптовая торговля эксплуатационными материалами и принадлежностями машин и оборудования', 'Оптовая  торговля   эксплуатационными    материалами   и принадлежностями машин и оборудования \nЭта группировка включает: \n- оптовую  торговлю   эксплуатационными  материалами   и принадлежностями, не включенными в  другие  группировки, для машин  и  оборудования,  относящихся  к  группировке 51.8,  например   деталями,  узлами  и  принадлежностями машин, не включенными в другие группировки', 1296, '51.87', 0);
