INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1300, '51.87.4', 'Оптовая торговля машинами и оборудованием для производства пищевых продуктов, напитков и табачных изделий', 'Оптовая   торговля    машинами   и   оборудованием   для производства  пищевых  продуктов,  напитков  и  табачных изделий', 1296, '51.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1301, '51.87.5', 'Оптовая торговля производственным электрическим оборудованием, машинами, аппаратурой и материалами', 'Оптовая    торговля    производственным    электрическим оборудованием, машинами, аппаратурой и материалами \nЭта группировка также включает: \n- оптовую торговлю проводами,  выключателями  и  прочими электроустановочными   изделиями    для    промышленного использования \n- оптовую торговлю прочими  электрическими  устройствами (электродвигателями, трансформаторами и т.п.) \n- оптовую торговлю промышленными роботами \n- оптовую    торговлю      измерительными     приборами, инструментами и оборудованием', 1296, '51.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1302, '51.87.6', 'Оптовая торговля прочими машинами, приборами, аппаратурой и оборудованием общепромышленного и специального назначения', 'Оптовая   торговля     прочими     машинами,  приборами, аппаратурой   и    оборудованием   общепромышленного   и специального назначения', 1296, '51.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1303, '51.88', 'Оптовая торговля машинами, оборудованием и инструментами для сельского хозяйства, включая тракторы', 'Оптовая торговля машинами, оборудованием и инструментами для сельского хозяйства, включая тракторы \nЭта группировка также включает: \n- оптовую    торговлю    машинами,    оборудованием    и инструментами для лесного хозяйства \n- оптовую торговлю газонокосилками с любым типом привода', 1284, '51.8', 2);
