INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1312, '52.11.2', 'Розничная торговля в неспециализированных магазинах незамороженными продуктами, включая напитки, и табачными изделиями', 'Розничная торговля  в   неспециализированных   магазинах незамороженными продуктами, включая напитки, и табачными изделиями', 1310, '52.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1313, '52.12', 'Прочая розничная торговля в неспециализированных магазинах', 'Прочая розничная    торговля    в   неспециализированных магазинах \nЭта группировка включает: \n- розничную   торговлю    универсальным    ассортиментом товаров,  в котором пищевые продукты, включая напитки, и табачные изделия не преобладают \n- деятельность     магазинов,     торгующих     товарами универсального  ассортимента,  в  том   числе   одеждой, мебелью,  бытовыми электротоварами, скобяными изделиями, косметическими    товарами,    ювелирными     изделиями, игрушками,   спортивными  товарами,  книгами,  газетами, журналами и т.п.', 1309, '52.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1314, '52.2', 'Розничная торговля пищевыми продуктами, включая напитки, и табачными изделиями в специализированных магазинах', 'Розничная торговля пищевыми продуктами, включая напитки, и табачными изделиями в специализированных магазинах', 1308, '52', 7);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1315, '52.21', 'Розничная торговля фруктами, овощами и картофелем', 'Розничная торговля фруктами, овощами и картофелем \nЭта группировка включает: \n- розничную торговлю свежими фруктами,  овощами, орехами и картофелем \nЭта группировка не включает: \n- розничную   торговлю    консервированными    фруктами, овощами, орехами и т.п., см. 52.27.33', 1314, '52.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1316, '52.22', 'Розничная торговля мясом, мясом птицы, продуктами и консервами из мяса и мяса птицы', 'Розничная торговля  мясом,  мясом  птицы,  продуктами  и консервами из мяса и мяса птицы \nЭта группировка также включает: \n- розничную  торговлю мясом животных и птиц,  являющихся объектом охоты (мясом дичи) \n- розничную  торговлю  продуктами  и  консервами из мяса дичи', 1314, '52.2', 3);
